	}
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	var table_name = &quot;&quot;;
	var refmod = _this.GetCellText(0,3,2);
	if (refmod == &quot;&quot;) {
		alert(&quot;请先选择分析模型！&quot;);
		return ;
	}
	_this.QuerySQLDS(&quot;select * from dim_model where guid=&apos;&quot;+refmod+&quot;&apos;&quot;);
	table_name = _this.XMLDS_GetString(0,&quot;SOURCEDS&quot;);

	if ((row == 8 || row == 9 ||row == 15||row == 16) && col == 2) {
		var selval = _this.GetCellText(sheet,row,2);
		var url =&quot;show.sp?grdid=DimSelectVal&table_name=&quot;+table_name+&quot;&selval=&quot;+selval;
		var ret = window.showModalDialog(url);
		if (ret != &quot;&quot; && ret != null ) _this.SetCellText(sheet,row,2,ret);
	}    

	if (row == 17 && col == 2) {
		var selval = _this.GetCellText(sheet,row,2);
		var value = new Object();
		var url =&quot;xlsgrid/jsp/pages/selectBox.jsp?flag=V_MID_PICNOTE&multi=1&quot;;
		var ret = window.showModalDialog( url, value, &quot;dialogwidth:610pt;dialogheight:420pt;help:yes;resizable:yes;&quot;);
		try {
			var sytid = _this.GetCellText(0, 4, 2);
			var noteid = _this.GetCellText(0, 5, 2);
			_sql.run(&quot;DELCHART&quot;, &quot;sytid=&quot; + sytid+&quot;&noteid=&quot;+noteid );
			for (var r = 0; r &lt; ret.length; r ++) 
			{
				var retid = ret[r];
				if (retid != &quot;&quot;) 
					_sql.run(&quot;INSCHART&quot;, &quot;sytid=&quot; + sytid+&quot;&noteid=&quot;+noteid+&quot;&chartid=&quot;+retid);
			}
			reloadthis();
		}catch ( ee ) {
			reloadthis();
		}
	}   
}


//单据列表加载完毕
function _thisOnpostloadListBill(sheet)
{
	var v_mod = &quot;&quot;;
	try {
		v_mod = selectBillGrid.CreateListValueByXMLDS(selectBillGrid.HttpCall(&quot;XmlDB.sp?bind=V_DIMMODEL&quot;),&quot;ID&quot;,&quot;NAME&quot;);
		for (var i=selectBillGrid.GetMainCellRangeRow1(sheet);i&lt;selectBillGrid.GetMainCellRangeRow2(sheet);i++) {
			selectBillGrid.SetToComboBox(&quot;&quot;,sheet,i,8,v_mod);
			selectBillGrid.SetCellHAlign(sheet,i,8,0);  	
		}

	}
	catch(e) {
//		alert(e);
	}
}


//下拉列表框选择改变
function _thisOnListSelChange(id, oldsel, cursel)
{
  if(id == &quot;CELL_0_14_2&quot;)
  {
  	if(_this.GetCellText(sheet,14,2) == &quot;1&quot;)
  	{
  		_this.SetRowVisible(sheet,15,1);
  		_this.SetRowVisible(sheet,16,1);
  		_this.SetRowVisible(sheet,17,1);
  		_this.SetRowVisible(sheet,18,1);  		
  	}	
  	else
  	{
  	  	_this.SetRowVisible(sheet,15,-1);
  		_this.SetRowVisible(sheet,16,-1);
  		_this.SetRowVisible(sheet,17,-1);
  		_this.SetRowVisible(sheet,18,-1);
  	} 	
  }

}

function reloadthis()
{
	_sql.querytods(&quot;SELECTCHART&quot;,&quot;sytid=&quot; + _this.GetCellText(sheet,4,2)+&quot;&noteid=&quot;+_this.GetCellText(sheet,5,2) );
	var names = &quot;&quot;;
	if(_this.XMLDS_GetRowCount() &gt; 0)
	{
		for(var row = 0;row &lt; _this.XMLDS_GetRowCount();row ++)
		{
			if(names != &quot;&quot;)
				names += &quot;,&quot;;
			var name = _this.XMLDS_GetString(row,&quot;name&quot;);	
			names += name;
		}
	}
	if(names != &quot;&quot;)
		_this.SetCellText(sheet,17,2,names);
	else
	{
		alert(&quot;为选择分析图的显示形式，请双击选择！&quot;);
		_this.SetCellText(sheet,17,2,&quot;&quot;);
	}

}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if ( id==&quot;保存&quot;) {
		f_save();
	}
	else if(id==&quot;新增子主题&quot;){

		window.location = &quot;show.sp?grdid=DIM_TOPIC&refid=&quot;+_this.GetCellText(0,5,2)+&quot;&selsytid=&quot;+_this.GetCellText(0,4,2)+&quot;&refmod=&quot;+_this.GetCellText(0,3,2)+&quot;&topicid=&quot;+_this.GetCellText(0,5,2)+&quot;_1&quot;;
	}
	else if(id==&quot;添加同层次的主题&quot;){

		window.location = &quot;show.sp?grdid=DIM_TOPIC&refid=&quot;+_this.GetCellText(0,12,2)+&quot;&selsytid=&quot;+_this.GetCellText(0,4,2)+&quot;&refmod=&quot;+_this.GetCellText(0,3,2);
	}
	else if(id==&quot;复制到新主题&quot;){

		window.location = &quot;show.sp?grdid=DIM_TOPIC&refid=&quot;+_this.GetCellText(0,12,2)+&quot;&selsytid=&quot;+_this.GetCellText(0,4,2)+&quot;&refmod=&quot;+_this.GetCellText(0,3,2)+&quot;&val1=&quot;+_this.GetCellText(0,8,2)+&quot;&val2=&quot;+_this.GetCellText(0,9,2)+&quot;&name=&quot;+_this.GetCellText(0,6,2)+&quot;&lognam=&quot;+_this.GetCellText(0,7,2);
	}

}
