		url: &apos;http://cdms.xmidware.com/aca/x.L_TreeNode.node.osp?id=&apos;+id,
		type: &apos;POST&apos;,
		error: function(){alert(&apos;Error loading PHP document&apos;);},
		success: function(result){
		document.getElementById(\&quot;inp\&quot;).value+=result +\&quot;&lt;font color=&apos;#FFFFFF&apos;&gt;&gt;&lt;/font&gt;\&quot;;
