//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
//	_thisOnCellModify(sheet,row,col,&quot;&quot;,&quot;&quot;);
	if((row == 15 || row == 8 || row == 11) && col == 2){
		var etlid = _this.GetCellText(0,2,2);
		if (etlid == &quot;&quot;){
			alert(&quot;ETL的编号不能为空，请输入！&quot;);
			return;
		}
		var func = _this.GetCellText(0,row,col);
		if (func == &quot;&quot;){
			if (row == 8)  func = &quot;afterExport&quot;;
			if (row == 11) func = &quot;afterTrans&quot;;
			if (row == 15) func = &quot;afterLoad&quot;;
		}
		var Url = &quot;show.sp?grdid=JSDOC&etlid=&quot;+etlid+&quot;&funcName=&quot; + func;
		var leftpos = window.screenLeft + 100;
		var args = new Array();

		ret = window.showModalDialog(Url,args,&quot;dialogLeft:&quot;+leftpos+&quot;px;dialogwidth:800px;dialogheight:700px;resizable:yes ;&quot;);
		_this.SetCellText(0,row,col,func);
		//_this.SetCellText(0,row,2,ret);
	}


}

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	var sh = _this.AddSheet(&quot;ETL定义说明&quot;);
	_this.SetToCodeEditor(&quot;note&quot;,sh,-1,-1,&quot;&quot;,&quot;&quot;,&quot;&quot;);	
	_this.ShowTabBar(1);  

	setNote();
	_this.SetCellLock(sh,-1,-1,1);
}

function setNote()
{
	var str = &quot;&quot;
+ &quot;\tETL编号	唯一标识一个ETL中间件，必填。	\r\n&quot;
+ &quot;\t名称			ETL名称。                                                                   \r\n&quot;
+ &quot;\t分类			ETL归属分类，可空。                                                         \r\n&quot;
+ &quot;\t说明			对该ETL的简单说明，可空。                                                   \r\n&quot;
+ &quot;\t                                                                                               \r\n&quot;
+ &quot;\t//第一步：从ftp或web下载                                                                       \r\n&quot;
+ &quot;\t类型			下载的类型，有两种方式：FTP和WEB。                                          \r\n&quot;
+ &quot;\t格式			下载的文件格式，有txt和xml两种，目前版本只支持txt。                             \r\n&quot;
+ &quot;\t分割符			txt文本的数据列的分割符号。                                                 \r\n&quot;
+ &quot;\t远程连接		FTP站点或WEB地址；如果是FTP站点，需在配置文件配置用户名和登录密码。       \r\n&quot;
+ &quot;\t			配置方式是站点名后接_user，_pwd和_path，分别是用户名，密码和目录路径；例如 127.0.0.1_user = xlsgrid。            \r\n&quot;
+ &quot;\t远程文件名		如下载类型是FTP，指定远程下载的文件名，留空时默认为ETL编号；WEB下载不需要指定。 \r\n&quot;
+ &quot;\t下载后删除文件		FTP下载后是否删除下载源文件，只有整个ETL流程正确无误转换成功才会删除。      \r\n&quot;
+ &quot;\t本地目录		远程下载的文件保存到本地的目录，未定义时默认为c:\\etldown。定义时路径请用反斜杠/，如d:/ftpdown                        \r\n&quot;
+ &quot;\t本地文件名		下载到本地后的文件名，留空时默认为ETL编号。                                 \r\n&quot;
+ &quot;\t触发函数		文件下载完成后执行的函数，请参考ETL编程说明 。                              \r\n&quot;
+ &quot;\t                                                                                               \r\n&quot;
+ &quot;\t//第二步：来源库（导入临时库并转换）                                                                     \r\n&quot;
+ &quot;\t表名称			导入到临时库的表名称，为空时系统自动创建并在整个过程结束后Drop掉。          \r\n&quot;
+ &quot;\t数据库			对应数据库指定的数据源名称，为空表示默认数据库。          \r\n&quot;
+ &quot;\tWHERE条件			从来源数据表查询的过滤WHERE子句。          \r\n&quot;
+ &quot;\t触发函数		导入到临时库完成后执行的函数，请参考ETL编程说明。                           \r\n&quot;
+ &quot;\t                                                                                               \r\n&quot;
+ &quot;\t//第三步：载入目标库                                                                           \r\n&quot;
+ &quot;\t目标表			导入到正式库的表名称，需指定。如果表不存在，则根据定义自动创建。            \r\n&quot;
+ &quot;\t数据库			对应数据库指定的数据源名称，为空表示默认数据库。          \r\n&quot;
+ &quot;\t唯一键			唯一索引列，UPDATE的条件。                                                 \r\n&quot;
+ &quot;\t触发函数		导入正式库完成后执行的函数，请参考ETL编程说明。                             \r\n&quot;
+ &quot;\t                                                                                               \r\n&quot;
+ &quot;\t//字段列表说明                                                                                 \r\n&quot;
+ &quot;\t编号			和文件数据列一一对应。                                                      \r\n&quot;
+ &quot;\t说明			对各数据列字段的说明，自动创建目标时作为目标表列字段的说明。                \r\n&quot;
+ &quot;\t类型			导入到临时表和目标表的字段数据类型，为空时默认为varchar2(20)。              \r\n&quot;
+ &quot;\t对应临时库的字段	临时表的字段名。                                                            \r\n&quot;
+ &quot;\t列转换计算公式		从临时表导入到正式表时的转换公式，所操作的是临时表的字段。                  \r\n&quot;
+ &quot;\t对应目标库的字段	目标表的字段列。                                                            \r\n&quot;;


	_this.SetText(&quot;note&quot;,str);
}

ROW.DATDSC:
drop table ETLHDR cascade constraints;
create table ETLHDR ( 
	GUID CHAR(32) default SYS_GUID()    not null ,
	ID VARCHAR2(20),
	NAME VARCHAR2(256),
	NOTE VARCHAR2(256),
	CLS VARCHAR2(20),
	EXPTYP VARCHAR2(20),
	EXPFMT VARCHAR2(20),
	EXPDIV VARCHAR2(20),
	EXPCON VARCHAR2(256),
	EXPCONFIL VARCHAR2(256),
	EXPLOCAL VARCHAR2(256),
	EXPLOCALFILE VARCHAR2(256),
	EXPFUNC VARCHAR2(256),
	TRANTAB VARCHAR2(256),
	TRANFUNC VARCHAR2(256),
	LOADTAB VARCHAR2(256),
	LOADTABKEY VARCHAR2(256),
	LOADFUNC VARCHAR2(256),
	EXPDEL VARCHAR2(4) default &apos;0&apos;
,
	SRCDB VARCHAR2(20),
	DSTDB VARCHAR2(20),
	SRCWHERE VARCHAR2(1024) );
  comment on column ETLHDR.GUID is &apos;内部唯一编号&apos; ;
 comment on column ETLHDR.ID is &apos;编号&apos; ;
 comment on column ETLHDR.NAME is &apos;名称&apos; ;
 comment on column ETLHDR.NOTE is &apos;说明&apos; ;
 comment on column ETLHDR.CLS is &apos;分类&apos; ;
 comment on column ETLHDR.EXPTYP is &apos;下载类型ftp或web&apos; ;
 comment on column ETLHDR.EXPFMT is &apos;下载格式txt或xml&apos; ;
 comment on column ETLHDR.EXPDIV is &apos;下载文件分割符&apos; ;
 comment on column ETLHDR.EXPCON is &apos;下载远程连接的站点&apos; ;
 comment on column ETLHDR.EXPCONFIL is &apos;下载远程连接的文件&apos; ;
 comment on column ETLHDR.EXPLOCAL is &apos;下载到本地目录&apos; ;
 comment on column ETLHDR.EXPLOCALFILE is &apos;下载到本地的文件名&apos; ;
 comment on column ETLHDR.EXPFUNC is &apos;下载触发事件&apos; ;
 comment on column ETLHDR.TRANTAB is &apos;临时库表名称&apos; ;
 comment on column ETLHDR.TRANFUNC is &apos;临时库触发函数&apos; ;
 comment on column ETLHDR.LOADTAB is &apos;正式库表名称&apos; ;
 comment on column ETLHDR.LOADTABKEY is &apos;正式库表的唯一字段列表&apos; ;
 comment on column ETLHDR.LOADFUNC is &apos;导入正式库触发函数&apos; ;
 comment on column ETLHDR.EXPDEL is &apos;下载后是否删除文件&apos; ;
 comment on column ETLHDR.SRCDB is &apos;来源数据库名&apos; ;
 comment on column ETLHDR.DSTDB is &apos;目标数据库名&apos; ;
 comment on column ETLHDR.SRCWHERE is &apos;来源表的查询WHERE条件&apos; ;
drop table ETLDTL cascade constraints;
