//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if(sheet==1 && row==2 && col==1 && newvalue!=&quot;&quot;){
		_this.SetCellText(0,7,11,_this.GetCellText(1,2,1));
		_this.SetCellShowText(0,7,11,_this.GetCellShowText(1,2,1));

		if(_this.GetCellText(0,7,11)==&quot;1&quot;){
			_this.SetRowVisible(0,13,-1);
			_this.SetRowVisible(0,14,-1);
			_this.SetRowVisible(0,15,1);	
			_this.SetRowVisible(0,16,1);
		}
		if(_this.GetCellText(0,7,11)==&quot;2&quot;){
			_this.SetRowVisible(0,13,1);
			_this.SetRowVisible(0,14,1);
			_this.SetRowVisible(0,15,-1);
			_this.SetRowVisible(0,16,-1);	
		}
		if(_this.GetCellText(0,7,11)==&quot;3&quot;){
			_this.SetRowVisible(0,13,1);
			_this.SetRowVisible(0,14,1);
			_this.SetRowVisible(0,15,1);
			_this.SetRowVisible(0,16,1);	
		}
		if(_this.GetCellText(0,7,11)==&quot;4&quot;){
			_this.SetRowVisible(0,13,1);
			_this.SetRowVisible(0,14,1);	
			_this.SetRowVisible(0,15,1);
			_this.SetRowVisible(0,16,1);
		}

	}
	if(sheet==1 && row==2 && col==1 && newvalue==&quot;&quot;){
			_this.SetCellText(0,7,11,&quot;&quot;);
			_this.SetCellShowText(0,7,11,&quot;&quot;);
			_this.SetRowVisible(0,13,1);
			_this.SetRowVisible(0,14,1);	
			_this.SetRowVisible(0,15,1);
			_this.SetRowVisible(0,16,1);
		}
}


