//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{

	if(sheet==0&&row==3&&col==13){
		if(_this.GetCellText(sheet,row,col)==&quot;&quot;){
			_this.SetToImage(&quot;&quot;,sheet,row,col,11);
			for( var i=row+2;i&lt;17;i++)
				_this.SetRowVisible(sheet,i,0);
			_this.SetCellText(sheet,row,col,&quot;1&quot;);
		}
		else {
			_this.SetToImage(&quot;&quot;,sheet,row,col,10);
			for( var i=row+2;i&lt;17;i++)
				_this.SetRowVisible(sheet,i,1);
			_this.SetCellText(sheet,row,col,&quot;&quot;);

		}

	}
}
