var sheet = 0;
var itmguid = &quot;&quot;;
var itmnoteguid = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	try{   itmguid = guid;  } catch(e){}
	if(itmguid!=&quot;&quot; ){

		var contentnote = _this.HttpCall(&quot;EAFormBlob.sp?action=download&guid=&quot;+itmguid );
		editor.html(contentnote);
	}
	_this.AddToolbarButton(&quot;Sure&quot;,&quot;确定&quot;,&quot;&quot;,&quot;&quot;,0,0,0,80);
}


function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if(id==&quot;Sure&quot;)
	{
		editor.sync();
		var contents = editor.html();
		contents = upLoadContentsImg(contents );
//		var path=document.getElementById(&quot;content1&quot;).value;
		var returnValue = f_upload(contents);
		alert ( &quot;保存成功&quot; );
		window.returnValue = returnValue ;
//		window.returnValue = contents ;
		window.close();
	}

}

function upLoadContentsImg(note)
{
	var p = note.indexOf(&quot;&lt;img src=&quot;,0);
	while (p != -1) {
		var imgsrc = note.substring(p+10,note.indexOf(&quot;\&quot;&quot;,p+10));
		if (imgsrc.indexOf(&quot;file://&quot;) &gt; -1) {
			var newurl = imgsrc.replace(&quot;file://&quot;,&quot;&quot;);
			var imgguid = saveImage(newurl,&quot;&quot;,G_SYTID,G_GRDID,imgsrc);
			note = note.replace(imgsrc,&quot;EAFormBlob.sp?guid=&quot;+imgguid);
		}
		if (imgsrc.indexOf(&quot;file:\\&quot;) &gt; -1) {
			var newurl = imgsrc.replace(&quot;file:\\&quot;,&quot;&quot;);
			var imgguid = saveImage(newurl,&quot;&quot;,G_SYTID,G_GRDID,imgsrc);
			note = note.replace(imgsrc,&quot;EAFormBlob.sp?guid=&quot;+imgguid);
		}
		p = note.indexOf(&quot;&lt;img src=&quot;,p+1);
	}
	return note;
}
//保存图片到数据库
//	srcurl	图片位置
function saveImage(srcurl)
{
	// 上传图片到formBlob表
	_this.OpenHttpRequest(homeurl+&quot;EAFormBlobUpload.sp&quot;,&quot;UPLOAD&quot;);
	_this.AddHttpRequestParam(&quot;action&quot;,&quot;upload&quot;,0);
	_this.AddHttpRequestParam(&quot;data&quot;,srcurl,1);
	_this.AddHttpRequestParam(&quot;formguid&quot;,&quot;&quot;,0);
	_this.AddHttpRequestParam(&quot;sytid&quot;,G_SYTID,0);
	_this.AddHttpRequestParam(&quot;grdid&quot;,G_GRDID,0);
	_this.AddHttpRequestParam(&quot;fileurl&quot;,srcurl,0);
	var ret = _this.SendHttpRequest();
	_this.CloseHttpRequest();

	return ret;
}

//上传一个文件到FORMBLOB
// 参数G_GUID：本表单的GUID
//返回FORMBLOB表的GUID
function f_upload(contents)
{
        var ret = &quot;&quot;;
        var tmpfile = _this.GetWindowTempFile();
	_this.SaveTextFile(tmpfile,contents,&quot;&quot;);
        _this.OpenHttpRequest(homeurl+&quot;EAFormBlobUpload.sp&quot;,&quot;UPLOAD&quot;);
        _this.AddHttpRequestParam(&quot;action&quot;,&quot;upload&quot;,0);
        _this.AddHttpRequestParam(&quot;data&quot;,tmpfile,1);
        _this.AddHttpRequestParam(&quot;formguid&quot;,itmguid,0);
        _this.AddHttpRequestParam(&quot;sytid&quot;,G_SYTID,0);
        _this.AddHttpRequestParam(&quot;grdid&quot;,G_GRDID,0);
        _this.AddHttpRequestParam(&quot;fileurl&quot;,tmpfile,0);

        ret=_this.SendHttpRequest();    
        _this.CloseHttpRequest(); 
        return ret ;
}

