ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	//每次打开时要先检查是不是对应的绑定有新加内容  
	invokeOSFunc(&quot;updateKaLoc&quot;);
	//设为CHECKBOX
	for (var row = 1;row&lt;_this.GetRowCount(sheet);row++)
	{
		for (var col = 2;col&lt;_this.GetColCount(sheet)-1;col++)
		{
			var rvalue = _this.GetCellText(sheet,row,0);
			var cvalue = _this.GetCellText(sheet,0,col);
			if(rvalue != &quot;&quot; && cvalue!=&quot;&quot;)
				_this.SetToBoolBox(sheet,row,col);
			if(rvalue ==&quot;&quot;)
				_this.SetRowVisible(sheet,row,-1);
			if(cvalue == &quot;&quot;)
			 	_this.SetColVisible(sheet,col,-1);


		}
	}
	//设置第一行行颜色
	for (col = 0;col&lt;_this.GetColCount(sheet)-1;col++)
	{
		_this.SetCellBkColor(sheet,0,col,193,210,240);
	}
	//设置工作区的背景色
	_this.SetCellBkColor(sheet,-1,-1,255,255,255);
} 

var obj = null;
var arr = new Array();
//保存
function Save()
{
	var len = arr.length;
	if(arr.length &gt; 0)
	{
		var xml = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;\n&quot;;
		    xml += &quot;&lt;ROWSET&gt;\n&quot;;
		for (var i=0;i&lt;arr.length;i++)
		{
			xml += &quot;&lt;ROW num=&apos;&quot;+i+&quot;&apos;&gt;\n&quot;;		
			xml += &quot;&lt;KAID&gt;&quot;+arr[i].kaid+&quot;&lt;/KAID&gt;\n&quot;;	
			xml += &quot;&lt;LOCID&gt;&quot;+arr[i].locid+&quot;&lt;/LOCID&gt;\n&quot;;
			xml += &quot;&lt;VALUE&gt;&quot;+arr[i].value+&quot;&lt;/VALUE&gt;\n&quot;;			
			xml += &quot;&lt;/ROW&gt;\n&quot;;
		}
		xml += &quot;&lt;/ROWSET&gt;\n&quot;;	
		var param = new Object();
		param.xml = xml;

		try{
			var msg = invokeOSFuncExt(&quot;saveUpdate&quot;,param);
			alert(&quot;渠道仓库绑定已成功，共修改&quot;+msg+&quot;条记录！&quot;);
			window.location.reload();		
		}catch(e){	
			alert(e);
		}  
	}
	else
	{
		alert(&quot;未修改任何渠道仓库绑定的行，请确认！&quot;);
		return;
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	obj = new Object();
	var cellValue = _this.GetCellText(sheet,row,col);
	if(cellValue == &quot;0&quot; || cellValue == &quot;1&quot;)
	{
		obj.kaid = _this.GetCellText(sheet,row,0);
		obj.locid = _this.GetCellText(sheet,0,col).split(&quot;-&quot;)[0];
		obj.value = cellValue;
		arr.push(obj);
	}	
	else 
		return;
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
function updateKaLoc()
{
	var db = null;
	var sql = &quot;&quot;;
	try{
		db = new pubpack.EADatabase();
		sql = &quot;
			insert into kaloc 
			select b.id kaid,b.name kanam,a.id locid,a.name locnam,&apos;0&apos; endflg 
			  from v_ebloc a,v_ka b 
			 where not exists ( select 1 from kaloc k where k.kaid = b.id and k.locid = a.id)
			 order by b.id,a.id		
		&quot;;
		db.ExcecutSQL(sql);
		db.Commit();		
	}catch(e){
		db.Rollback();
		throw new Exception(e);
	}
}

function saveUpdate()
{
	var db = null;
	var ds = null;
	var pstmt = null;
	var sql = &quot;&quot;;
	var i = 0;
	try{
		db = new pubpack.EADatabase();
		ds = new pubpack.EAXmlDS(xml);		
		sql = &quot; update kaloc set endflg=? where kaid=? and locid=?&quot;;
		pstmt = db.GetConn().prepareStatement(sql);
		if ( ds.getRowCount()&gt;0 ){
			for (var r = 0;r&lt; ds.getRowCount();r++)
			{				
				pstmt.setInt(1,Integer.parseInt(ds.getStringAt(i,&quot;VALUE&quot;)));
				pstmt.setString(2,ds.getStringAt(i,&quot;KAID&quot;));
				pstmt.setString(3,ds.getStringAt(i,&quot;LOCID&quot;));
				pstmt.addBatch();
				i++;
			}
			pstmt.executeBatch();		
		}
		db.Commit();
		return i;
	}catch(e){
		db.Rollback();
		throw new Exception(e);
	}

}

ROW.DATDSC:
select kaid 渠道编号,kanam 渠道名称,locid||&apos;-&apos;||locnam 仓库,endflg 
  from kaloc
 where kaid like &apos;[%KAID]%&apos;
   and locid like &apos;[%LOCID]%&apos;
 order by kaid,locid
