//批量导入数据
function IMP()
{

}

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetCellText(sheet, 3, 7, G_LOGDAT);
}

//SelectBox控件修改单元格内容
function _thisOnSelectBoxCellModify(sheet,row,col,oldvalue,newvalue,key,where)
{
	if (row &gt; 4 && row &lt; _this.GetRowCount(sheet) - 2) {
		if (col == 1) {
			var itemid = _this.GetCellText(sheet, row, 3);
			if (itemid != &quot;&quot;) {
				_sql.querytods(&quot;SELCORPITEM&quot;, &quot;itemid=&quot; + itemid + &quot;&kaid=&quot; + newvalue);
				var price = _this.XMLDS_GetString(0, &quot;price&quot;);
				var spec = _this.XMLDS_GetString(0, &quot;spec&quot;);
				var note = _this.XMLDS_GetString(0, &quot;note&quot;);
				_this.SetCellText(sheet, row, 5, price);
				_this.SetCellText(sheet, row, 6, spec);
				_this.SetCellText(sheet, row, 7, note);
			}
		} else if (col == 3) {
			var kaid = _this.GetCellText(sheet, row, 1);
			if (kaid != &quot;&quot;) {
				_sql.querytods(&quot;SELCORPITEM&quot;, &quot;itemid=&quot; + newvalue + &quot;&kaid=&quot; + kaid);
				var price = _this.XMLDS_GetString(0, &quot;price&quot;);
				var spec = _this.XMLDS_GetString(0, &quot;spec&quot;);
				var note = _this.XMLDS_GetString(0, &quot;note&quot;);
				_this.SetCellText(sheet, row, 5, price);
				_this.SetCellText(sheet, row, 6, spec);
				_this.SetCellText(sheet, row, 7, note);
			} else {
				_sql.querytods(&quot;SELITEM&quot;, &quot;itemid=&quot; + newvalue);
				var price = _this.XMLDS_GetString(0, &quot;price&quot;);
				var spec = _this.XMLDS_GetString(0, &quot;spec&quot;);
				_this.SetCellText(sheet, row, 5, price);
				_this.SetCellText(sheet, row, 6, spec);
			}
		}
	}
	if(row==3&&col==2){
//		alert(&quot;xx&quot;);
		var row1= _this.GetMainCellRangeRow1(sheet);
		var row2= _this.GetMainCellRangeRow2(sheet);
//		alert(row1);
		var v_id = _this.GetCellText(sheet,row,col);
//		var v_name = _this.GetCellText(sheet,5,2);
		_sql.querytods(&quot;GetVndName&quot;,&quot;ID=&quot;+v_id);
		var v_name =  _this.XMLDS_GetString(0, &quot;name&quot;);

		_this.GetCellText(sheet,5,2);
		for(var r=row1;r&lt;=row2;r++){
			_this.SetCellText(sheet,r,1,v_id);
			_this.SetCellText(sheet,r,2,v_name);
		}
	}
}

//设置渠道一致
function SetToAll()
{
 	      var sheet = 0;
		var row1= _this.GetMainCellRangeRow1(sheet);
		var row2= _this.GetMainCellRangeRow2(sheet);
//		alert(row1);
		var v_id = _this.GetCellText(sheet,5,1);
		var v_name = _this.GetCellText(sheet,5,2);
//		_sql.querytods(&quot;GetVndName&quot;,&quot;ID=&quot;+v_id);
//		var v_name =  _this.XMLDS_GetString(0, &quot;name&quot;);

		_this.GetCellText(sheet,5,2);
		for(var r=row1;r&lt;=row2;r++){
			_this.SetCellText(sheet,r,1,v_id);
			_this.SetCellText(sheet,r,2,v_name);
		}
}

//保存单据数据后
function _thisOnaftersave(success)
{

  	var bilid = &quot;&quot;;
  	var RETVAL  =&quot;&quot;;
	if((&quot;true&quot;==_this.XMLDS_GetString(0,&quot;_ServerMsg_&quot;))&&(&quot;1&quot; == _this.XMLDS_GetString(0,&quot;CODE&quot;)))
	{ 
		RETVAL = _this.XMLDS_GetString(0,&quot;RETVAL&quot;);
		bilid = RETVAL.split(&quot;,&quot;)[0];
		alert(bilid );
		_sql.querytods(&quot;sel&quot;,&quot;bilid=&quot;+bilid+&quot;&biltyp=&quot;+G_GRDID);
		alert(_this.XMLDS_GetRowCount());
		alert(bilid);

  	}
  	return success ;

}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );

//单据审核前
function fos_beforecheck(eaContext)
{
	var db = null;
	var sql = &quot;&quot;;

	try {
		db = new pubpack.EADatabase();	// 如果连接到其他数据库, new pubpack.EADatabase(“dbname”)
		var xmlhdr = new pubpack.EAXmlDS(eaContext.getXmlhdr());
		var xmldtl = new pubpack.EAXmlDS(eaContext.getXmldtl());
		var crtusr = xmlhdr.getStringAt(0, &quot;CRTUSR&quot;);
		var crtdat = xmlhdr.getStringAt(0, &quot;DAT&quot;);
		for (var i = 0; i &lt; xmldtl.getRowCount(); i ++) {
			var kaid = xmldtl.getStringAt(i, &quot;ITEMPC&quot;);
			var itemid = xmldtl.getStringAt(i, &quot;ITMID&quot;);
			if (kaid != &quot;&quot; && itemid != &quot;&quot;) {
				sql = &quot;select count(*) count from corpitem where CORP=(select guid from v_ka where id=&apos;&quot;+ kaid
					+&quot;&apos;) and item=(select guid from v_item where id=&apos;&quot;+ itemid +&quot;&apos;)&quot;;
				var ds = db.QuerySQL(sql);
				var price = xmldtl.getStringAt(i, &quot;PRICE&quot;);
				var note = xmldtl.getStringAt(i, &quot;REFNAM2&quot;);
				if (ds.getStringAt(0, &quot;count&quot;)*1 &gt; 0) {
					sql = &quot;update corpitem set price=&quot;+ price +&quot;,note=&apos;&quot;+ note +&quot;&apos;&quot;
						+&quot; where CORP=(select guid from v_ka where id=&apos;&quot;+ kaid
						+&quot;&apos;) and item=(select guid from v_item where id=&apos;&quot;+ itemid +&quot;&apos;)&quot;;
//						throw new Exception(sql);
				} else {
					sql = &quot;insert into corpitem(item,CORP,price,note,crtusr,crtdat) values((select guid from v_item where id=&apos;&quot;+ itemid
						+&quot;&apos;),(select guid from v_ka where id=&apos;&quot;+ kaid +&quot;&apos;),&quot;+ price +&quot;,&apos;&quot;+ note +&quot;&apos;,&apos;&quot;+ crtusr +&quot;&apos;,to_date(&apos;&quot;+ crtdat +&quot;&apos;,&apos;yyyy-mm-dd&apos;))&quot;;
				}
//				throw new Exception(sql);
				db.ExcecutSQL(sql);
			} else {
				throw new pubpack.EAException ( &quot;商品、渠道不可为空！！！&quot; );
			}
		}
		db.Commit();
	} catch ( ee ) {
		db.Rollback();
		throw new pubpack.EAException ( ee.toString() );
	} finally {
		if (db!=null) db.Close();
	}

}
