//载入ZXG之后
function _thisOnpost_LoadZXGFile(sheet)
{

	_this.SplitSheet(0,&quot;V&quot;,200);
	_this.SplitSheet(1,&quot;H&quot;,800);
	_this.SetToSVGForm(&quot;svgform1&quot;,2,-1,-1,&quot;&quot;);
	_this.SetDesignMode(1);
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if(id==&quot;查询文件&quot; ) {
		var srcdir = _this.GetCellText(0,3,2);
		var srcfile = _this.GetCellText(0,3,7);
		var xml = _this.FindFile(srcdir+&quot;\\&quot;+srcfile );
		_this.SetMainCellRange(1,1,0,5,8);
		_this.SetXmlDS(1,1,0,5,8,xml);
		for(var i=_this.GetMainCellRangeCol1(1);i&lt;=_this.GetMainCellRangeCol2(1);i++){
			_this.SetCellText(1,0,i,_this.GetColName(1,i) );
		}
	}
	else 	if(id==&quot;生成&quot; ) {
		var destdir = _this.GetCellText(0,5,2);
		var destfile = _this.GetCellText(0,5,7);
		var seq = 0;
		_this.SetAttribe(&quot;&quot;,_this.ATTR_EXPORT_IMAGE_FORMAT,_this.GetCellText(0,7,7));
		for(var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++){
			var fileurl = _this.GetCellText(1,r,1);

			var svg = &quot;&lt;svg&gt;&lt;img src=\&quot;&quot;+fileurl+&quot;\&quot; width=\&quot;&quot;+_this.GetCellText(0,7,2)+&quot;\&quot; height=\&quot;&quot;+_this.GetCellText(0,7,4)+&quot;\&quot;&gt;&quot;;
			_this.SetToSVGForm(&quot;svgform1&quot;, 2, -1, -1 , svg );
			_this.SVGFORM_SetClipWH(&quot;svgform1&quot;, 0,0,_this.GetCellText(0,7,2),_this.GetCellText(0,7,4));
			var destfileurl = destdir +&quot;\\&quot;+destfile+seq +&quot;.&quot;+_this.GetCellText(0,7,7);
			_this.SetToolbarString(&quot;正在转换&quot;+fileurl+&quot;到&quot;+destfileurl  );
			//_this.svgform_s	
			alert (destfileurl  );
			var retfile = _this.SVGFORM_SaveImageFile(&quot;svgform1&quot;,destfileurl , -1,-1,-1,-1);	
			alert ( retfile);	
			seq++;
			//Sleep(1000);
			//break;
			_this.SetRedraw(1);
		}	
		var srcdir = _this.GetCellText(0,3,2);
		var srcfile = _this.GetCellText(0,3,7);
		var xml = _this.FindFile(srcdir+&quot;\\&quot;+srcfile );
		_this.SetMainCellRange(1,1,0,5,8);
		_this.SetXmlDS(1,1,0,5,8,xml);
		for(var i=_this.GetMainCellRangeCol1(1);i&lt;=_this.GetMainCellRangeCol2(1);i++){
			_this.SetCellText(1,0,i,_this.GetColName(1,i) );
		}
	}


	else 	if(id==&quot;选择&quot;) {
		var ret = _this.SelectDir();
		if(ret!=&quot;&quot;)
			_this.SetCellText(0,row,2,ret);
	}
}

