	}
  	// 显示工具栏
	_this.Showtoolbar(1);
	// 显示分页栏
	_this.ShowTabBar(1);
        var sheet = 0 ;
//        _this.SetSheetName(sheetTree,&quot;数据库表&quot;);
//       if ( treexml == null ) 
//             alert ( &quot;无法载入表对象&quot; );
//       else
//         _this.SetToTreeCtrl(&quot;Tree_1&quot;,sheetTree ,-1,-1,treexml);

//        treexml = invokeOSFunc(&quot;GetSytList&quot;,&quot;G_SYTID=&quot;+G_SYTID ) ;      
//
//        _this.SetToTreeCtrl(&quot;tree_2&quot;,sheetMW,-1,-1,&quot;&lt;?xml version =  &apos;1.0&apos;?&gt;&lt;中间件对象&gt;&quot;+treexml+&quot;&lt;/中间件对象&gt;&quot;);

	_sql.querytods(&quot;GetHLList&quot;,&quot;thisdeforg=&quot;+G_DEFORG);
	var list3 = _this.CreateListValue();
	for (var i=0;i&lt;_this.XMLDS_GetRowCount();i++){
		_this.SetListValue(list3,_this.XMLDS_GetString(i,&quot;ID&quot;),_this.XMLDS_GetString(i,&quot;NAME&quot;));
	}
	_this.SetToComboBox(&quot;layobjclass&quot;, sheetMW,1,2,list3);
	_this.SetCellText(sheetMW,1,2,&quot;ACAWEB&quot;);
	_thisOnListSelChange(&quot;layobjclass&quot;,&quot;&quot;,&quot;ACAWEB&quot;);

        //alert(HTMLHEAD); 
        _this.SetSheetName(sheetMW,&quot;页面&quot;);
        _this.SetSheetName(sheetDatflw,&quot;业务流-图&quot;);
        _this.SetDesignMode(1);

	if(xmlfile==&quot;&quot;) {
		_this.SetToSVGForm(&quot;flow_1&quot;,sheetDatflw,-1,-1 ,&quot;&quot;);
		_this.SetAttribe(&quot;flow_1&quot;,&quot;DCLICKOPENPROPWND&quot;,&quot;0&quot;);	//默认不打开图元编辑窗口，而是数据库编辑窗口
	}
	else {
		var param = &quot;selsytid=&quot;+ selsytid+&quot;&filename=&quot;+ xmlfile;
		var xml = invokeOSFunc(&quot;loadDataflwGraph&quot;,param);
		_this.SetToSVGForm(&quot;flow_1&quot;,sheetDatflw,-1,-1 ,xml);
	}
	//_this.SetAttribe(&quot;flow_1&quot;,&quot;DCLICKOPENPROPWND&quot;,&quot;1&quot;);	// 双击打开属性窗口


	_this.SetSheetName(sheetDatflw,&quot;业务流图&quot;+xmlfile);



