	_this.SetSheetName(sheetDatflwDoc,&quot;图示说明&quot; );
	_this.SetToCodeEditor(&quot;rtf_doc&quot;,sheetDatflwDoc,-1,-1,&quot;&quot;,&quot;&quot;,&quot;&quot;);


	_this.SplitSheet(&quot;0&quot;,&quot;H&quot;,&quot;260&quot;);

	var menuxml = &quot;&lt;item id=\&quot;m_new_view\&quot; name=\&quot;新增视图\&quot;/&gt;&lt;item id=\&quot;m_new_flow\&quot; name=\&quot;新增子流图\&quot;/&gt;&quot;;
