var G_DROPROW =&quot;&quot;;
//================================================================//  
// 函数：_thisOnBeginDrag
// 说明：开始拖动
//================)_================================================// 
function _thisOnBeginDrag(id)  
{ 
	window.status = &quot;开始拖动&quot;+id ;
	var idsplit= id.split(&quot;_&quot; );
	var _sheet=idsplit[1]
	var _row=idsplit[2]
	var _col=idsplit[3]
	if(_row&gt;=3&&_this.GetCellText(_sheet,_row,7)!=&quot;&quot;){
		G_DROPROW = idsplit[2]; 
	}
}
//================================================================// 
// 函数：_thisOnEndDrop
// 说明：
// 参数：拖动到终点,id为目标点编号
//================================================================// 
function _thisOnEndDrop(id,text)  
{ 
	var x=text.split(&quot;,&quot;)[0];
	var y=text.split(&quot;,&quot;)[1];
	var idsplit= id.split(&quot;_&quot; );
	var _sheet=idsplit[1];
	if(_sheet==sheetDatflw){
		var img= _this.GetCellText(sheetMW,G_DROPROW,1);
		var name=_this.GetCellText(sheetMW,G_DROPROW,2);
		var id=_this.GetCellText(sheetMW,G_DROPROW,3);
		var title=_this.GetCellText(sheetMW,G_DROPROW,4);
		var deforg =_this.GetCellText(sheetMW,G_DROPROW,5);
		var url =_this.GetCellText(sheetMW,G_DROPROW,6);
		var guid=_this.GetCellText(sheetMW,G_DROPROW,7);
		var svg = &quot;&lt;rect id=\&quot;&quot;+id+&quot;\&quot;  round=\&quot;5\&quot; selgrdid=\&quot;L\&quot; selsytid=\&quot;&quot;+G_SYTID+&quot;\&quot; selgrdtyp=\&quot;F\&quot;  fill=\&quot;#EEEEEE\&quot;   fillto=\&quot;#FFFFFF\&quot; x=\&quot;&quot;+x+&quot;\&quot; y=\&quot;&quot;+y+&quot;\&quot; width=\&quot;120\&quot;  height=\&quot;60\&quot; selectflag=\&quot;mouseclick\&quot;  menuid=\&quot;-1\&quot; url=\&quot;show.sp?grdid=L&datacc=&quot;+G_ACCID+&quot;&guid=&quot;+guid+&quot;&accid=&quot;+G_ACCID+&quot;\&quot; xmlmenu=\&quot;[%left]item name=[%quot]修改页面属性[%quot] id=[%quot]m_hml:&quot;+guid+&quot;[%quot]/[%right]\&quot; &gt;&quot;+name+&quot;&lt;/rect&gt;&quot;;				 						
		_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);

	}

}


//下拉列表框选择改变
function _thisOnListSelChange(id, oldsel, cursel)
{
	if(id==&quot;layobjclass&quot;){
		var endrow = _this.GetRowCount(sheetMW)-2;
		var clsid = _this.GetCellText(sheetMW,1,2);
		_this.SetAttribe(&quot;SHEET_&quot;+sheetMW,&quot;LINESPLIT&quot;,&quot;~&quot; ) 
		for ( var i=3;i&lt;=endrow;i++){
			_this.SetToStandardCell(sheetMW,i,1);
			_this.SetCellText(sheetMW,i,1,&quot;&quot;);
			_this.SetCellText(sheetMW,i,2,&quot;&quot;);
			_this.SetCellText(sheetMW,i,3,&quot;&quot;);
			_this.SetCellText(sheetMW,i,4,&quot;&quot;);
			_this.SetCellText(sheetMW,i,5,&quot;&quot;);
			_this.SetCellText(sheetMW,i,6,&quot;&quot;);
		}
		xml = _sql.query(&quot;GetLAYHDR&quot;,&quot;thisdeforg=&quot;+G_DEFORG+&quot;&clsid=&quot;+clsid );
//		alert(xml);
		_this.SetXmlDS(sheetMW,3,1,endrow-1,7,xml);
		endrow = _this.GetRowCount(sheetMW)-3;
		for ( var i=3;i&lt;=endrow;i++){
			var img = _this.GetCellText(sheetMW,i,1);
			if(img!=&quot;&quot;)
				_this.SetCellText(sheetMW,i,1,&quot;[%IMAGE src=\&quot;&quot;+img+&quot;\&quot; imgfilltyp=\&quot;3\&quot; value=\&quot; \&quot; ]&quot; );// =0 拉伸铺完，=1原始但不大于可视区间=2 原始大小可能会超出区间=3等比例铺完 =4 强制左对齐=5 强制上对齐=6 强制右对齐=7 强制下对齐
			_this.SetRowHeight(sheetMW,i,60);	
			if(_this.GetCellText(sheetMW,i,2)==&quot;&quot;)_this.SetRowVisible(sheetMW,i,0);
			else _this.SetRowVisible(sheetMW,i,1);
		}
		//_this.SetColWidth(sheetMW,1,60);		
	}

}

function del()
{
  if(candel!=&quot;1&quot;)
  {
    alert(&quot;不能删除系统配置文件.&quot;);
    return;
  }
  if(confirm(&quot;确实要删除这个项目吗？&quot;))
  {
    var msg = _this.invokeOSFunc (&quot;delBilDef&quot;,&quot;xmlfile=&quot;+xmlfile+&quot;&isDatFlw=&quot;+candel);
    alert(msg);
  }
}

