var SEQNUM=0;
function _thisOnTreeItemDClick(id, hItem, tagid, attribe, value)
{
      var collist = &quot;&quot;;
      var action = getXmlAttribute(attribe,&quot;action&quot;);
      var conn  = getXmlAttribute(attribe,&quot;conn&quot;);
      var name=getXmlAttribute(attribe,&quot;name&quot;);
      var nextlevel_itemid = &quot;&quot;;
      try {nextlevel_itemid =_this.XMLCTL_GetItem(id,hItem,2);}catch ( e ) {nextlevel_itemid =&quot;ddd&quot;;}

      var level = _this.XMLCTL_GetLevel(id,hItem);
      var itemid = tagid;      // 标签
      if(conn!=null) G_DSID=conn;
      if (G_DSID == &quot;&quot;) G_DSID = &quot;默认&quot;;

	 if ( id==&quot;Tree_1&quot; ) {
		      _this.SetToolbarString(&quot;当前数据源: &quot;+G_DSID);
		      var level = _this.XMLCTL_GetLevel(id,hItem);

		       if ( action == &quot;TABLE&quot; ) {

		            xml = invokeOSFunc(&quot;GetTableGroupTree&quot;,&quot;tagid=TABLE表&dsid=&quot; + G_DSID+&quot;&sqlid=&quot;+G_SQLID) ;

		            _this.XMLCTL_Append(id,xml,hItem,2);
		      }
			else if ( action == &quot;VIEW&quot; ) {
				xml = invokeOSFunc(&quot;GetTableGroupTree&quot;,&quot;tagid=VIEW视图&dsid=&quot; + G_DSID+&quot;&sqlid=&quot;+G_SQLID) ;

		           	_this.XMLCTL_Append(id,xml,hItem,2);

 			}
 			else if ( action == &quot;TABLEGROUP&quot; ) {
				var groupid= getXmlAttribute(attribe,&quot;groupid&quot;);

		            xml = invokeOSFunc(&quot;GetTableTree&quot;,&quot;pretablname=&quot;+groupid+&quot;&tagid=TABLE表&dsid=&quot; + G_DSID+&quot;&sqlid=&quot;+G_SQLID) ;

		            _this.XMLCTL_Append(id,xml,hItem,2);
		      }
			else if ( action == &quot;VIEWGROUP&quot; ) {
				var groupid= getXmlAttribute(attribe,&quot;groupid&quot;);
				xml = invokeOSFunc(&quot;GetTableTree&quot;,&quot;pretablname=&quot;+groupid+&quot;&tagid=VIEW视图&dsid=&quot; + G_DSID+&quot;&sqlid=&quot;+G_SQLID) ;

		           	_this.XMLCTL_Append(id,xml,hItem,2);

 			}
		       else if( action==&quot;ADDTABLE&quot; ){
		      		SEQNUM++;
				var gid = tagid; 	
				var xmlmenu = &quot;[%left]item name=[%quot]修改该数据表结构[%quot] id=[%quot]m_table_:&quot;+conn+&quot;:&quot;+tagid+&quot;[%quot]/[%right]&quot;;

				var svg = &quot;&lt;rect id=\&quot;&quot;+gid+&quot;\&quot;  dbname=\&quot;&quot;+G_DSID+&quot;\&quot; tablename=\&quot;&quot;+tagid+&quot;\&quot; fill=\&quot;#E0F0F8\&quot;  round=\&quot;5\&quot; x=\&quot;20\&quot; y=\&quot;20\&quot; width=\&quot;80\&quot;  height=\&quot;60\&quot; url=\&quot;\&quot; xmlmenu=\&quot;&quot;+xmlmenu +&quot;\&quot; &gt;&quot;+tagid+&quot;\r\n&quot;+name+&quot;&lt;/rect&gt;&quot;;				 						
				_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);
		      }
		      else if( action==&quot;ADDVIEW&quot; ){
		      		SEQNUM++;
				var gid = tagid; 	
				var xmlmenu = &quot;[%left]item name=[%quot]修改该视图[%quot] id=[%quot]m_view_:&quot;+conn+&quot;:&quot;+tagid+&quot;[%quot]/[%right]&quot;;

				var svg = &quot;&lt;rect id=\&quot;&quot;+gid+&quot;\&quot;  dbname=\&quot;&quot;+G_DSID+&quot;\&quot; tablename=\&quot;&quot;+tagid+&quot;\&quot; fill=\&quot;#E9E9FF\&quot;  round=\&quot;5\&quot; x=\&quot;20\&quot; y=\&quot;20\&quot; width=\&quot;80\&quot;  height=\&quot;60\&quot; url=\&quot;\&quot; xmlmenu=\&quot;&quot;+xmlmenu +&quot;\&quot; &gt;&quot;+tagid+&quot;\r\n&quot;+name+&quot;&lt;/rect&gt;&quot;;				 						
				_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);
		      }

	 }
	 else {//中间件
	    var thisselsytid =getXmlAttribute(attribe,&quot;sytflg&quot;);
	    var todo = getXmlAttribute(attribe,&quot;todo&quot;);

            grdtyp =getXmlAttribute(attribe,&quot;selgrdtyp&quot;);

            if ( todo==&quot;adddbflow&quot; ) {
            	var ss = itemid.split(&quot;.&quot;);

            	addflowitem(thisselsytid , ss[0] , itemid +&quot;的名称&quot;); 

            }
            else if ( grdtyp ==&quot;D&quot; ) {//数据流图 selgrdtyp=\&quot;D\&quot;
            	if ( nextlevel_itemid.length== 0 ) {
                        var md_xml = &quot;&quot;;
                        try {
                        	md_xml = invokeOSFunc (&quot;GetDBFlowList&quot;,&quot;selsytid=&quot; + thisselsytid ) ;
                        	if ( md_xml == &quot;&quot; || md_xml == undefined  ) alert ( &quot;该系统下面没有定义数据流图&quot; );
                        	else _this.XMLCTL_Append(id,md_xml,hItem,2);
                        }
                        catch ( e ) {
                        	alert ( e );
                        }

                  }

            }

            else if ( sytid.length&gt;0 && level == 3 ) {      // 展开tree节点

                  if ( nextlevel_itemid.length== 0 ) {
                        var md_xml = &quot;&quot;;
                        try {
                        	md_xml = invokeOSFunc (&quot;GetGrdList&quot;,&quot;selsytid=&quot; + thisselsytid +&quot;&selgrdtyp=&quot;+grdtyp) ;
                        	if ( md_xml == &quot;&quot; || md_xml == undefined  ) alert ( &quot;该系统下面没有定义中间件&quot; );
                        	else _this.XMLCTL_Append(id,md_xml,hItem,2);
                        }
                        catch ( e ) {
                        	alert ( e );
                        }

                  }
            }
            else if ( attribe.length &gt; 0 && level&gt;=4 ) {   
                	// 打开某个数据流
                	var thisselsytid =_this.XMLCTL_GetAttribe (id,hItem,&quot;sytflg&quot;);
                	var bilid  =_this.XMLCTL_GetAttribe (id,hItem,&quot;id&quot;);
                	var bilnam  =_this.XMLCTL_GetAttribe (id,hItem,&quot;name&quot;);
                	var selgrdtyp = _this.XMLCTL_GetAttribe (id,hItem,&quot;selgrdtyp&quot;);

			addgrditem(thisselsytid ,bilid  ,grdtyp , bilnam  ) ;



            }


	 }

}

//================================================================// 
// 函数：getXmlAttribute
// 说明：返回XML节点字符串的某个属性值
// 参数：attribe 参数字符串，如 ID=&quot;101&quot; NAME=&quot;scott&quot;  
//       attribename 参数名称 如 ID
// 返回：如 101
// 样例：
// 作者：
// 创建日期：03/11/06 21:18:02
// 修改日志：
//================================================================// 
function getXmlAttribute(attribe,attribename)
{

            var loc = attribe.indexOf( attribename+&quot;=\&quot;&quot; );
            var loc1 = attribe.indexOf( &quot;\&quot;&quot; ,  loc+attribename.length+2);
            var ret = attribe.substring( loc+attribename.length+2,loc1  ) ;
            return ret;

}
function save()
{

		//建立一个参数对象
		  var param = new Object();
		  if ( selsytid == &quot;&quot; ) {
		  	var fileid= prompt( &quot;请输入要保存的流程图所在系统编号&quot;,selsytid   );
		  	if ( fileid==undefined ) return;
		  	selsytid = fileid;
		  }
		  CURFILENAME =xmlfile;
//		  alert(CURFILENAME);
		  if ( CURFILENAME == &quot;&quot; ) {
		  	var fileid= prompt( &quot;请输入要保存的流程图编号&quot; ,&quot;&quot;);
		  	if ( fileid==undefined) return;
		  	CURFILENAME = fileid+&quot;.webflow&quot;;
		  }

		  var docStr = _this.GetCellText(sheetDatflwDoc,-1,-1);
		  if (docStr != &quot;&quot;) {
		  	var sdir = &quot;xmldb/grddb/syt&quot;+selsytid+&quot;/doc/&quot;;
			var sfile = &quot;&quot;+xmlfile+&quot;.html&quot;;

			var filename = _this.SaveTextFileToTempDir(docStr);
			// 上传文件到xlsGridData
			var saveUrl = &quot;UploadToXlsGridData.sp&quot;;
			_this.OpenHttpRequest(saveUrl,&quot;UPLOAD&quot;);
			_this.AddHttpRequestParam(&quot;uploadfile&quot;, filename,1);//本地文件的路径
			_this.AddHttpRequestParam(&quot;path&quot;,sdir  ,0);	//远程保存的目录
			_this.AddHttpRequestParam(&quot;filename&quot;,sfile ,0);//注意这里只能是文件名，用于远程保存的文件名
			_this.AddHttpRequestParam(&quot;handle&quot;,&quot;REPORTSTORE&quot;,0);
			var ret=_this.SendHttpRequest(); 
			_this.CloseHttpRequest();
			var loc1 = ret.indexOf( &quot;&lt;TOPIC&quot; );
			var loc2 = ret.indexOf( &quot;&lt;/TOPIC&quot; );
			var str = ret.substring(loc1+7,loc2);
		}

		  param.xmlfile=CURFILENAME ;
		  param.selsytid = selsytid;
		  param.xmlstr = _this.GetText(&quot;flow_1&quot;);

		  var msg = invokeOSFuncExt(&quot;save&quot;,param);
                  alert(msg);

}            


//另存为
function saveas()
{
	if ( CURFILENAME != &quot;&quot; ) {
		  //建立一个参数对象
		  var param = new Object();
	  	  var fileid= prompt( &quot;请输入要保存的流程图所在系统编号&quot;,selsytid  );
	  	  if ( fileid==undefined) return;
	  	  selsytid = fileid;

	  	  var fileid= prompt( &quot;请输入要保存的流程图编号&quot;,&quot;&quot;  );
	  	  if ( fileid==undefined) return;
	  	  CURFILENAME = fileid+&quot;.dbflow&quot;;
		  param.xmlfile=CURFILENAME ;

		  param.selsytid = selsytid;
		  param.xmlstr = _this.GetText(&quot;flow_1&quot;);
		  var msg = invokeOSFuncExt(&quot;save&quot;,param);
                  alert(msg);
	 }	

}

//从代码刷新图
function updatefromsrc()
{
		_this.SetRedraw(0);
		_this.SetToSVGForm(&quot;flow_1&quot;,sheetDatflw,-1,-1 ,_this.GetCellText(sheetDatflwSrc  ,-1,-1));
		_this.SetAttribe(&quot;flow_1&quot;,&quot;DCLICKOPENPROPWND&quot;,&quot;1&quot;);	// 双击打开属性窗口

		_this.SetRedraw(1);

}

//试运行
function testrun()
{
	window.open( &quot;flow.jsp?flowid=&quot;+CURFILENAME +&quot;&syt=&quot;+selsytid  );

}

function _thisOnSheetShow(sheet,name)
{
  	if ( sheet== sheetDatflwSrc  ){
            var rows = _this.GetRowCount(0);
            var cols = _this.GetColCount(0);
            _this.SetCellText( sheetDatflwSrc  ,-1,-1, _this.GetCellText(sheetDatflw,-1,-1) );
	}
}

//导出文档
//typ =0 数据库 = 1 概要设计 =2 详细设计 =3 代码 =4 测试
function exportDoc(typ)
{
	_this.SVGFORM_SetStatusText(&quot;flow_1&quot;,&quot;正在导出文档，请稍候...&quot;,-1);//.SVGFORM_SetTitleText
	_this.SetRedraw(1);

	setTimeout(&quot;_exportDoc(&apos;&quot;+typ+&quot;&apos;)&quot;, 500 );
}
