//得到生成WORD文档的第一层的HTML标签
function GetDocLevelHtml1(title)
{
	return &quot;&lt;h2 style=&apos;margin-left:0cm;text-indent:0cm&apos;&gt;&lt;a name=&apos;_Toc334046963&apos;&gt;&lt;![if !supportLists]&gt;&lt;span style=&apos;mso-bidi-font-family:微软雅黑&apos;&gt;&lt;span style=&apos;mso-list:Ignore&apos;&gt;第一章 &lt;/span&gt;&lt;/span&gt;&lt;![endif]&gt;&quot;+title+&quot;&lt;/a&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/h2&gt;&quot;;
}

//得到生成WORD文档的第二层的HTML标签
function GetDocLevelHtml2(seq,title)
{
	return &quot;&lt;h3 style=&apos;text-indent:0cm&apos;&gt;&lt;a name=&apos;_Toc334046964&apos;&gt;&lt;/a&gt;&lt;a name=&apos;_Toc25378447&apos;&gt;&lt;/a&gt;&lt;a name=&apos;_Toc530853555&apos;&gt;&lt;span style=&apos;mso-bookmark:_Toc25378447&apos;&gt;&lt;span style=&apos;mso-bookmark:_Toc334046964&apos;&gt;&lt;![if !supportLists]&gt;&lt;span style=&apos;mso-bidi-font-family:微软雅黑&apos;&gt;&lt;span style=&apos;mso-list:Ignore&apos;&gt;&quot;+seq+&quot;. &lt;/span&gt;&lt;/span&gt;&lt;![endif]&gt;&quot;+title+&quot;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/h3&gt;&quot;;
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	var ss = menuitemid.split(&quot;:&quot;);	//m_flow_

	if(ss[0]==&quot;m_hml&quot;){

		openWindow( &quot;show.sp?grdid=L&datacc=&quot;+G_ACCID+&quot;&guid=&quot;+ss[1]+&quot;&accid=&quot;+G_ACCID);

	}
	if ( ss .length &gt;=4 && ss[0].substring(0,6)==&quot;m_grd_&quot;) {

		var selsytid=ss[2];
		var selgrdid=ss[3];
		var selgrdnam = &quot;&quot;;
		if(ss .length &gt;=5)selgrdnam  = ss[4];

		//判断目前系统是否存在该中间件
		// 通过Get的方式调用服务端的一个函数
		var cnt = invokeOSFunc(&quot;queryMW&quot;,&quot;sSytid=&quot;+selsytid+&quot;&sGrdid=&quot;+selgrdid ) ; 
		var url = &quot;xlsgrid/jsp/design/designer.jsp?grdid=&quot;+selgrdid+&quot;&sytid=&quot;+selsytid;
		if(cnt==&quot;0&quot; ) {
			url = &quot;xlsgrid/jsp/design/designer.jsp?todo=new&MWTYP=&quot;+ss[1].substring(0,1) +&quot;&selsytid=&quot;+selsytid+&quot;&grdid=&quot;+selgrdid  ;//+&quot;&grdnam=&quot;+encodeURIComponent(selgrdnam)
		}
		openWindow(url);
	}
	else if ( ss .length &gt;=2 && ss[0].substring(0,8)==&quot;m_table_&quot;) {
		var conn=ss[1];
		var tableid=ss[2];
		openWindow( &quot;show.sp?grdid=edittable&table_name=&quot;+tableid+&quot;&connid=&quot;+conn);
	}
	else if ( ss .length &gt;=2 && ss[0].substring(0,7)==&quot;m_view_&quot;) {	//编辑视图
		var conn=ss[1];
		var tableid=ss[2];
		openWindow( &quot;show.sp?grdid=edittable&tabtype=VIEW&table_name=&quot;+tableid+&quot;&connid=&quot;+conn);
	}
	else if ( ss .length &gt;=2 && ss[0].substring(0,7)==&quot;m_flow_&quot;) {	//编辑子图
		var selsytid=ss[1];
		var selid=ss[2];

		openWindow( &quot;show.sp?grdid=LHTMLFLOW&xmlfile=&quot;+selid+&quot;.dbflow&selsytid=&quot;+selsytid );
	}
	else if ( menuitemid == &quot;m_export_ss&quot; ) {//需求说明
		exportDoc(5);
	}

	else if ( menuitemid == &quot;m_export_table&quot; ) {//数据结构
		exportDoc(0);
	}
	else if ( menuitemid == &quot;m_export_sa&quot; ) {//概要设计
		exportDoc(1);
	}
	else if ( menuitemid == &quot;m_export_sd&quot; ) {//详细设计
		exportDoc(2);
	}
	else if ( menuitemid == &quot;m_export_src&quot; ) {//代码文档
		exportDoc(3);
	}
	else if ( menuitemid == &quot;m_export_xmlout&quot; ) {//
		exportDoc(5);
	}

	else if ( menuitemid == &quot;m_export_test&quot; ) {//测试报告
		exportDoc(4);
	}
	else if ( menuitemid == &quot;m_new_table&quot; ) {//数据结构
		var tagid = prompt(&quot;请输入TABLE的编号&quot;,&quot;&quot;);if (tagid == undefined ) return;
		var name = prompt(&quot;请输入TABLE的名称&quot;,tagid+&quot;的名称&quot;);if (name == undefined ) return;	
		addtableitem(tagid , name ) ;
	}
	else if ( menuitemid == &quot;m_new_view&quot; ) {//view数据结构
		var tagid = prompt(&quot;请输入VIEW的编号&quot;,&quot;&quot;);if (tagid == undefined ) return;
		var name = prompt(&quot;请输入VIEW的名称&quot;,tagid+&quot;的名称&quot;);if (name == undefined ) return;	
		addviewitem(tagid , name ) ;
	}
	else if ( menuitemid == &quot;m_new_m_mw&quot; ) {//自定义
		addgrditem(G_SYTID,&quot;&quot;,&quot;M&quot;,&quot;&quot;);
	}
	else if ( menuitemid == &quot;m_new_b_mw&quot; ) {//自定义
		addgrditem(G_SYTID,&quot;&quot;,&quot;B&quot;,&quot;&quot;);
	}
	else if ( menuitemid == &quot;m_new_f_mw&quot; ) {//自定义
		addgrditem(G_SYTID,&quot;&quot;,&quot;F&quot;,&quot;&quot;);
	}
	else if ( menuitemid == &quot;m_new_q_mw&quot; ) {//自定义
		addgrditem(G_SYTID,&quot;&quot;,&quot;Q&quot;,&quot;&quot;);
	}
	else if ( menuitemid == &quot;m_new_r_mw&quot; ) {//自定义
		addgrditem(G_SYTID,&quot;&quot;,&quot;R&quot;,&quot;&quot;);
	}
	else if ( menuitemid == &quot;m_new_flow&quot; ) {//flow\
		var tagid = prompt(&quot;请输入子图的编号&quot;,&quot;&quot;);if (tagid == undefined ) return;
		var name = prompt(&quot;请输入子图的名称&quot;,tagid+&quot;的名称&quot;);if (name == undefined ) return;	
		addflowitem(G_SYTID,tagid ,name );
	}
}
//新增一个table的图元
//tagid:table的编号
function addtableitem(tagid , name ) 
{
		SEQNUM++;
		var gid = tagid; 	
		var xmlmenu = &quot;[%left]item name=[%quot]修改该数据表结构[%quot] id=[%quot]m_table_:&quot;+G_DSID+&quot;:&quot;+tagid+&quot;[%quot]/[%right]&quot;;
		var svg = &quot;&lt;rect id=\&quot;&quot;+gid+&quot;\&quot;  dbname=\&quot;&quot;+G_DSID+&quot;\&quot; tablename=\&quot;&quot;+tagid+&quot;\&quot; fill=\&quot;#E0F0F8\&quot;  round=\&quot;5\&quot; x=\&quot;20\&quot; y=\&quot;20\&quot; width=\&quot;80\&quot;  height=\&quot;60\&quot; url=\&quot;\&quot; xmlmenu=\&quot;&quot;+xmlmenu +&quot;\&quot; &gt;表:&quot;+tagid+&quot;\r\n&quot;+name+&quot;&lt;/rect&gt;&quot;;				 						
		_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);
}
//新增一个view的图元
//tagid:view的编号
function addviewitem(tagid , name ) 
{
		SEQNUM++;
		var gid = tagid; 	
		var xmlmenu = &quot;[%left]item name=[%quot]修改该视图结构[%quot] id=[%quot]m_view_:&quot;+G_DSID+&quot;:&quot;+tagid+&quot;[%quot]/[%right]&quot;;
		var svg = &quot;&lt;rect id=\&quot;&quot;+gid+&quot;\&quot;  dbname=\&quot;&quot;+G_DSID+&quot;\&quot; viewname=\&quot;&quot;+tagid+&quot;\&quot; fill=\&quot;#B9FFB9\&quot;  round=\&quot;5\&quot; x=\&quot;20\&quot; y=\&quot;20\&quot; width=\&quot;80\&quot;  height=\&quot;60\&quot; url=\&quot;\&quot; xmlmenu=\&quot;&quot;+xmlmenu +&quot;\&quot; &gt;视图:&quot;+tagid+&quot;\r\n&quot;+name+&quot;&lt;/rect&gt;&quot;;				 						
		_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);
}

//新增一个中间件的图元
//bilid  :中间件编号
function addgrditem(thisselsytid ,bilid  ,selgrdtyp , bilnam  ) 
{
		SEQNUM++;
		if(bilid == &quot;&quot; ) {
			bilid  = prompt(&quot;请输入中间件的编号&quot;,&quot;&quot;);if (bilid  == undefined ) return;
		}
		if (bilnam  ==&quot;&quot; ) {
			bilnam  = prompt(&quot;请输入中间件的名称&quot;,&quot;&quot;);if (bilnam  == undefined ) return;
		}
		if (grdtyp ==&quot;&quot; ) {
			grdtyp = prompt(&quot;请输入中间件的类型&quot;,&quot;&quot;);if (grdtyp == undefined ) return;
		}
		if (thisselsytid ==&quot;&quot; ) {
			thisselsytid = prompt(&quot;请输入系统的编号&quot;,&quot;&quot;);if (thisselsytid == undefined ) return;
		}
		var gid = grdtyp +SEQNUM+&quot;:&quot;+thisselsytid +&quot;:&quot;+bilid+&quot;:&quot;+bilnam    ; 
		var grdtypname = &quot;&quot;;if (selgrdtyp ==&quot;M&quot;)grdtypname =&quot;基础&quot;;else if (selgrdtyp ==&quot;Q&quot;)grdtypname =&quot;查询&quot;;if (selgrdtyp ==&quot;R&quot;)grdtypname =&quot;报表&quot;;if (selgrdtyp ==&quot;B&quot;)grdtypname =&quot;单据&quot;;if (selgrdtyp ==&quot;F&quot;)grdtypname =&quot;表单&quot;;
		var xmlmenu = &quot;[%left]item name=[%quot]修改该中间件[%quot] id=[%quot]m_grd_:&quot;+gid +&quot;[%quot]/[%right]&quot;;
		var svg = &quot;&lt;rect id=\&quot;&quot;+gid+&quot;\&quot;  round=\&quot;5\&quot; selgrdid=\&quot;&quot;+bilid+&quot;\&quot; selsytid=\&quot;&quot;+thisselsytid +&quot;\&quot; selgrdtyp=\&quot;&quot;+selgrdtyp +&quot;\&quot;  fill=\&quot;#EEEEEE\&quot;   fillto=\&quot;#FFFFFF\&quot; x=\&quot;120\&quot; y=\&quot;20\&quot; width=\&quot;120\&quot;  height=\&quot;60\&quot; selectflag=\&quot;mouseclick\&quot;  menuid=\&quot;-1\&quot; url=\&quot;entry.sp?grdid=&quot;+bilid  +&quot;\&quot; xmlmenu=\&quot;&quot;+xmlmenu+&quot;\&quot; &gt;&quot;+grdtypname +&quot;:&quot;+bilid  +&quot;\r\n&quot;+bilnam  +&quot;&lt;/rect&gt;&quot;;//				 						
		_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);

}
//新增一个子图的图元
//tagid:table的编号
function addflowitem(selsytid, tagid , name ) 
{
	SEQNUM++;
	var gid = tagid; 	
	var xmlmenu = &quot;[%left]item name=[%quot]打开该子图[%quot] id=[%quot]m_flow_:&quot;+selsytid+&quot;:&quot;+tagid+&quot;[%quot]/[%right]&quot;;
	var svg = &quot;&lt;polygon id=\&quot;flow_&quot;+gid+&quot;\&quot; selsytid=\&quot;&quot;+selsytid+&quot;\&quot;  points=\&quot;193,113 181,113 180,120 193,113 193,46 54,46 54,120 180,120 193,113\&quot; flowid=\&quot;&quot;+tagid+&quot;\&quot; flowname=\&quot;&quot;+name+&quot;\&quot; changeto=\&quot;page\&quot; stroke=\&quot;#000000\&quot; fill=\&quot;#FFFFCC\&quot;  xmlmenu=\&quot;&quot;+xmlmenu +&quot;\&quot; &gt;子图:&quot;+tagid+&quot;\r\n&quot;+name+&quot;&lt;/polygon&gt; &quot;;

	_this.SVGFORM_AppendXml(&quot;flow_1&quot;,svg,&quot;&quot;);
}

