//================================================================// 
// 函数：thisOnpost_loaddata
// 说明：调用dbhelper的初始化动作
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：11/17/05 17:31:52
// 修改日志：
//================================================================// 
function _thisOnpost_loaddata(sheet)
{
       setButton();
       // 构造3个 SQL
       // 查询仓库数据表记录
       selectSQL = &quot;SELECT GUID,ORG as 组织编号,ID as 仓库编号,NAME as 仓库名称,LONGNAME as 全称,ACC 帐套号,LOCTYP as 分类, &quot;+
                        &quot;SUBACC 代储公司, &quot;+//, SUBLOCID 代存仓库
                        &quot;ADDR as 地址,MAN as 仓库主管,TEL as 电话,FAX as 传真, &quot;+
                        &quot;DEFSLT1 as 默认库位1码头默认收货仓位, &quot;+
                        &quot;DEFSLT2 as 默认库位2月台默认收货仓位, &quot;+
                        &quot;DEFSLT3 as 默认库位3销售退货仓位, &quot;+
                        &quot;DEFSLT4 as 默认库位4加工的合格品, &quot;+
                        &quot;DEFSLT5 as 默认库位5加工的不合格品, &quot;+
                        &quot;DEFSLT6 as 默认库位6加工原料库位投料区, &quot;+
                        &quot;DEFSLT7 as 默认库位7出货的默认库位 &quot;+
                        &quot;from LOC where  LOCTYP in (&apos;0&apos;,&apos;3&apos;) and ORG =&apos;&quot;+G_ORGID+&quot;&apos;&quot;;
       //新增仓库数据表记录
       insertSQL = &quot;insert into LOC(GUID,ORG,ID,NAME,LONGNAME,ACC,LOCTYP,SUBACC,ADDR,MAN,TEL,FAX,&quot;+
                  &quot;DEFSLT1,DEFSLT2,DEFSLT3,DEFSLT4,DEFSLT5,DEFSLT6,DEFSLT7 &quot;+
                  //,CRTUSR,CRTDAT 
                  &quot; )  VALUES( sys_guid(),?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,? )  &quot;;
       // 修改仓库数据表记录
       updateSQL = &quot;update LOC set org=?,id=?,name=?,LONGNAME=?,acc=?,LOCTYP=?,SUBACC=?,ADDR=?,MAN=?,TEL=?,FAX=?, &quot;+
                   &quot;DEFSLT1=?,DEFSLT2=?,DEFSLT3=?,DEFSLT4=?,DEFSLT5=?,DEFSLT6=?,DEFSLT7=? &quot;+
                   //,CRTUSR=?,CRTDAT=to_date(?,&apos;YYYY-MM-DD&apos;)  &quot;+
                   &quot;where guid=? &quot;;
       //删除仓库数据表记录
       deleteSQL = &quot;delete LOC where guid=? &quot;;            

       _dbhelper.SetSelectSQL( selectSQL );
       _dbhelper.SetInsertSQL( insertSQL );
       _dbhelper.SetUpdateSQL( updateSQL );
       _dbhelper.SetDeleteSQL( deleteSQL );

       // 载入数据并放在相应的位置
       _dbhelper.Load(1,1,2,2);
}

