function f_dbeditpostloaddata(){
      _this.SetColVisible(sheet,3,-1);
       // 自定义的特殊处理
       // 如：
       //       1、设置列的邦定属性
       //       2、设置默认值
       //       3、设置可修改或隐藏属性
       var listid0 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_LOCTYP&quot;),&quot;ID&quot;,&quot;NAME&quot;);
       var acclist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCLIST&quot;),&quot;ID&quot;,&quot;NAME&quot;);

       for(row=_this.GetMainCellRangeRow1(sheet);row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
             {
                  var str = _this.GetCellText(sheet,row,8);
                  _this.SetToComboBox(&quot;&quot;, sheet,row,8,listid0);
                  _this.SetToComboBox(&quot;&quot;, sheet,row,9,acclist );
              }

}

function f_dbeditcellmodify(sheet,row,col,oldvalue,newvalue){

      if( sheet==0 && row&gt;=1 && col==4 ){
            _this.SetCellText(sheet,row,3,orgid);
            _this.SetCellText(sheet,row,4,orgid+newvalue);
      }

}

ROW.GRDOSDS_VALUE:
//基本数据维护保存数据前
//脚本在此进行操作校验，比如验证数据是否可以安全删除
//action: + 插入; U 更新; - 删除
//datads: 维护界面传过来的数据
//rowno: 当前要操作的数据行
function beforeSaveDbData(action,eadb,datads,rowno)
//var eadb = new pub.EADatabase(); var datads = new EAXmlDS();
{
  var guid = datads.getStringAt(rowno,&quot;guid&quot;);
  var orgid=datads.getStringAt(rowno,&quot;组织编号&quot;);
  var locid=datads.getStringAt(rowno,&quot;仓库编号&quot;);
  if(guid == &quot;&quot;) return;
  var oldid;
  var usecount;
  //try{
  	oldid = eadb.GetSQL(&quot;select id from LOC where guid=&apos;%s&apos;&quot;.format([guid]));
  	usecount = eadb.GetSQL(&quot;select count(*) from bilhdr where org=&apos;%s&apos; and locid=&apos;%s&apos;&quot;.format([orgid,oldid]));
  //}catch(e) {return;}
  if( usecount&gt;0)
    if( action==&quot;-&quot; || (action==&quot;U&quot; && oldid != locid) )
    {
      throw new Exception(&quot;仓库%s已经使用,不能修改或删除该仓库。&quot;.format([oldid]));
    }
}
