//标题类型修改后
function titlemode_modify()
{
	var ROW1= 17;var ROW2=24;
	var titlemode = _this.GetCellText(0,15,3);
	_this.SetRedraw(0);
	if (titlemode == &quot;STD&quot; ) {
		for ( var r=ROW1;r&lt;=ROW1+1;r++ )_this.SetRowVisible(0,r,0);
		for ( var r=ROW1+2;r&lt;=ROW2;r++ )_this.SetRowVisible(0,r,1);
	}
	else if (titlemode == &quot;HTML&quot; ) {
		for ( var r=ROW1;r&lt;=ROW1+1;r++ )_this.SetRowVisible(0,r,1);
		for ( var r=ROW1+2;r&lt;=ROW2;r++ )_this.SetRowVisible(0,r,0);
	}
	else {
		for ( var r=ROW1;r&lt;=ROW2;r++ )_this.SetRowVisible(0,r,0);
	}
	_this.SetRedraw(1);	
}
//BODY类型修改后
function bodymode_modify()
{
	var ROW1= 27;var ROW2=41;
	var titlemode = _this.GetCellText(0,27,3);
	_this.SetRedraw(0);
//	_this.SetListValue(list1,&quot;HTML&quot;,&quot;HTML&quot;);
//	_this.SetListValue(list1,&quot;OS&quot;,&quot;OS脚本&quot;);
//	_this.SetListValue(list1,&quot;SQL&quot;,&quot;SQL&quot;);
//	_this.SetListValue(list1,&quot;MW&quot;,&quot;中间件界面&quot;);	
//	_this.SetListValue(list1,&quot;FRAME&quot;,&quot;内部FRAME&quot;);
//	_this.SetListValue(list1,&quot;FRAMEGROUP&quot;,&quot;分页FRAME组&quot;);	
//	_this.SetListValue(list1,&quot;SCROLLIMAGE&quot;,&quot;滚动图片&quot;);
//	_this.SetListValue(list1,&quot;TOOLBAR&quot;,&quot;系统默认工具栏&quot;);		
//	_this.SetListValue(list1,&quot;MENU&quot;,&quot;菜单栏&quot;);	
//	_this.SetListValue(list1,&quot;FLASH&quot;,&quot;视频演示&quot;);	
//	_this.SetListValue(list1,&quot;表格&quot;,&quot;表格(标准)&quot;);
//	_this.SetListValue(list1,&quot;空白表格&quot;,&quot;空白表格&quot;);	

	for ( var r=ROW1;r&lt;=ROW2;r++ )_this.SetRowVisible(0,r,0);
	if (titlemode == &quot;HTML&quot; ) {
		for ( var r=39;r&lt;=ROW2+1;r++ )_this.SetRowVisible(0,r,1);
	}
	else if (titlemode == &quot;OS&quot; ) {
		for ( var r=35;r&lt;=36;r++ )_this.SetRowVisible(0,r,1);
	}
	else if (titlemode == &quot;MW&quot; ) {
		for ( var r=34;r&lt;=35;r++ )_this.SetRowVisible(0,r,1);
	}
	else if (titlemode == &quot;FRAME&quot;||titlemode == &quot;LOGIN&quot; ) {
		for ( var r=36;r&lt;=37;r++ )_this.SetRowVisible(0,r,1);
	}
	else {
		for ( var r=29;r&lt;=33;r++ )_this.SetRowVisible(0,r,1);
//		for ( var r=29;r&lt;=35;r++ )_this.SetRowVisible(0,r,1);
	}
	if(_this.GetCellText(0,43,10)!=titlemode)
		deletemaincell(0);
	_this.SetCellText(0,43,10,titlemode)
	var xml=_sql.query(&quot;getcslb&quot;,&quot;id=&quot;+titlemode );
	_this.SetXmlDS(0,_this.GetMainCellRangeRow1(0),_this.GetMainCellRangeCol1(0),_this.GetMainCellRangeRow2(0),_this.GetMainCellRangeCol2(0),xml);
	_this.SetRedraw(1);	
}
//清空主数据区域
function deletemaincell(sheet){
	for(var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++){
		for(var c=_this.GetMainCellRangeCol1(0);c&lt;=_this.GetMainCellRangeCol2(0);c++){
			_this.SetCellText(sheet,r,c,&quot;&quot;);
		}
	}
}
//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{

	if(id==&quot;选择&quot;){
//		var retval=window.showModalDialog(&quot;iconbatch.sp?rettyp=WEBPATH&quot;,&quot;&quot;,&quot;dialogHeight=500pt; dialogWidth=800pt;status=no&quot;); 
//		if (retval!=&quot;&quot; && retval!=undefined )
//			_this.SetCellText(sheet,row,col-2, retval );
	}
	else if(id==&quot;样例&quot;){
		var html= &quot;&lt;table border=\&quot;0\&quot; width=\&quot;100%\&quot; bgcolor=\&quot;#000080\&quot; height=\&quot;30\&quot; cellspacing=\&quot;0\&quot; cellpadding=\&quot;0\&quot;&gt;&lt;tr&gt;&lt;td width=\&quot;30\&quot;&gt;&lt;img border=\&quot;0\&quot; src=\&quot;xlsgrid/images/flash/icon/icon_3.png\&quot; width=\&quot;30\&quot; height=\&quot;30\&quot;&gt;&lt;/td&gt;&lt;td&gt;&nbsp;&lt;font color=\&quot;#FFFFFF\&quot; size=\&quot;3\&quot; &gt;TITLE&lt;/font&gt;&lt;/td&gt;	&lt;/tr&gt;&lt;/table&gt;&quot;;
		if(_this.GetCellText(0,17,3)==&quot;&quot;)_this.SetCellText(0,17,3,html);
		else if ( confirm( &quot;样例会覆盖现有的内容，是否继续？&quot; ) == 1) _this.SetCellText(0,17,3,&quot;&quot;);
	}
	else if ( id==&quot;HTML编辑器&quot; ) {
		var guid=_this.GetCellText(sheet,row,3);
		var ret = window.showModalDialog(&quot;show.sp?grdid=HtmlEdit&guid=&quot;+guid,null,&quot;dialogwidth:1024pt;dialogheight:600pt;help:yes;resizable:yes;&quot;);
		if(ret!=undefined)
		{
			_this.SetCellText(sheet,row,3,ret);
			_this.SetCellShowText(sheet,row,3,_this.HttpCall(&quot;EAFormBlob.sp?guid=&quot;+ret));
		}
	}
	else if ( id == &quot;显示风格&quot; ) {

		_sql.querytods(&quot;GetGuid&quot;,&quot;id=&quot;+_this.GetCellText(sheet,row,col+1));
		openWindow(&quot;show.sp?grdid=LHTMLEDIT&guid=&quot;+_this.XMLDS_GetString(0,&quot;GUID&quot;) );
	}
	else if ( id == &quot;编辑WEB 控件&quot; ) {
		_sql.querytods(&quot;GetGuid&quot;,&quot;id=&quot;+_this.GetCellText(1,3,3));
		openWindow(&quot;show.sp?grdid=LHTMLEDIT&guid=&quot;+_this.XMLDS_GetString(0,&quot;GUID&quot;) );
	}

	else if ( id==&quot;数据来源&quot; ) {
		openWindow(&quot;show.sp?grdid=LSYSURLED&loaddata=1&menu=&quot;+_this.GetCellText(sheet,row,col+1) );

	}
	else if(id==&quot;OS中间件&quot;){

		var sytmw = _this.GetCellText(sheet,row,3);
		var idx = sytmw.indexOf(&quot;_&quot;);

		if(idx&gt;0){
			var thissytid = sytmw.substring(0,idx);
			var thismwid = sytmw.substring(idx+1);
			var func = _this.GetCellText(sheet,row,6);
			openWindow(&quot;xlsgrid/jsp/design/designer.jsp?grdid=&quot;+thismwid +&quot;&sytid=&quot;+thissytid +&quot;&opensheet=2&addosfunc=&quot;+func +&quot;()&sAddosfuncnote= &quot;);
		}
	}
	else if(id==&quot;显示所有的属性&quot;){
		showallrow();
	}


}
//显示所有的属性
function showallrow(){

	if(_this.GetRowVisible(0,7)==1){
		for(var i=7;i&lt;=42;i++){
			if(i!=27) _this.SetRowVisible(0,i,0);
		}

	}
	else {
		for(var i=7;i&lt;=42;i++){
			if(i!=27) _this.SetRowVisible(0,i,1);
		}
	}
	titlemode_modify();
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{

	var id=menuitemid;
	//&quot;&lt;item id=\&quot;m_img_select\&quot; name=\&quot;选择图片\&quot;/&gt;&lt;item id=\&quot;m_img_modify\&quot; name=\&quot;编辑图片\&quot;/&gt;&lt;item id=\&quot;m_img_preview\&quot; name=\&quot;预览图片\&quot;/&gt;&quot;;

	if(id==&quot;m_img_modify&quot;){
		var imgurl = _this.GetCellText(sheet,row,col-1);
		var imgguid = &quot;&quot;;
		var prestr = &quot;EAFormBlob.sp?guid=&quot;;
		if(imgurl.substring(0,prestr.length)==prestr){
			imgguid = imgurl.substring(prestr.length );
		}
		try{
			var bw = _this.GetCellText(sheet,3,8);
			var bh = _this.GetCellText(sheet,3,9);
			if(bh==&quot;&quot;||row!==7)bh= &quot;120&quot;;
			if(bw==&quot;&quot;||row!==7)bw= &quot;120&quot;;

			var url = &quot;show.sp?grdid=ImageEdit&formguid=&quot;+imgguid +&quot;&bigimgw=&quot;+bw+&quot;&bigimgh=&quot;+bh+&quot;&imgw=&imgh=&smlimgw=&smlimgh=&quot;;

			var ret = window.showModalDialog(url,&quot;&quot;,&quot;dialogwidth:1024pt;dialogheight:600pt;help:yes;resizable:yes;&quot;);
			if(ret!=undefined){

				var ss = ret.split(&quot;,&quot;);
				_this.SetCellText(sheet,row,col-1,prestr+ss[0]);
				if(ss.length&gt;=5&&row==7){
					_this.SetCellText(sheet,3,8,ss[3]);
					_this.SetCellText(sheet,3,9,ss[4]);
				}
			}
		}catch(e){}
	}
	else if(id==&quot;m_img_select&quot;){
		var retval=window.showModalDialog(&quot;iconbatch.sp?rettyp=WEBPATH&quot;,&quot;&quot;,&quot;dialogHeight=500pt; dialogWidth=800pt;status=no&quot;); 
		if (retval!=&quot;&quot; && retval!=undefined ){
			_this.SetCellText(sheet,row,col-1, retval );
		}
	}
	else if(id==&quot;m_img_selectlib&quot;){
		var retval=window.showModalDialog(&quot;LAYIMG.sp&quot;,&quot;&quot;,&quot;dialogHeight=500pt; dialogWidth=800pt;status=no&quot;); 
		if (retval!=&quot;&quot; && retval!=undefined ){
			SetImageURL (sheet,row,col,retval );	//skinguid=
		}
	}	
	else if(id==&quot;m_img_preview&quot;){
		previewimg(_this.GetCellText(sheet,row,col-1));
	}
	else if(id==&quot;m_img_upload&quot;){//选择本地图片上传
		var filename = _this.SelectFile(&quot;&quot;);
		if (filename !=&quot;&quot; ) {
			_this.OpenHttpRequest(homeurl+&quot;EAFormBlobUpload.sp&quot;,&quot;UPLOAD&quot;);
			_this.AddHttpRequestParam(&quot;action&quot;,&quot;upload&quot;,0);
			_this.AddHttpRequestParam(&quot;data&quot;,filename ,1);
			_this.AddHttpRequestParam(&quot;formguid&quot;,&quot;&quot;,0);
			_this.AddHttpRequestParam(&quot;sytid&quot;,G_SYTID,0);
			_this.AddHttpRequestParam(&quot;grdid&quot;,G_GRDID,0);
			_this.AddHttpRequestParam(&quot;fileurl&quot;,filename ,0);
			var ret = _this.SendHttpRequest();
			_this.CloseHttpRequest();
			if(ret!=&quot;&quot; ) {
				_this.SetCellText(sheet,row,col-1, &quot;EAFormBlob.sp?guid=&quot;+ret);
			}
		}
	}
	else if ( id==&quot;HTML编辑器&quot; ) {
		var guid=_this.GetCellText(sheet,row,3);
		var ret = window.showModalDialog(&quot;show.sp?grdid=HtmlEdit&guid=&quot;+guid,null,&quot;dialogwidth:1024pt;dialogheight:600pt;help:yes;resizable:yes;&quot;);
		if(ret!=undefined)
		{
			_this.SetCellText(sheet,row,3,ret);
			_this.SetCellShowText(sheet,row,3,_this.HttpCall(&quot;EAFormBlob.sp?guid=&quot;+ret));
		}
	}





}
function previewimg(url){
	window.open(url);
	//var w = window.open(&quot;&quot;,&quot;&quot;,&quot;width:800;height:600&quot;);
	//w.document.body.innerHTML=&quot;&lt;img src=&apos;http://www.xlsgrid.net/xlsgrid/ROOT_XLSGRID/&quot;+url+&quot;&apos;&gt;&quot;;
}


//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{

	if(sheet==0 && row==15 && col ==3 ) titlemode_modify();
	if(sheet==0 && row==27 && col ==3 ) bodymode_modify();
	if(sheet==1 && row==5 ) {//子类的所有显示风格
		if(col==3){
			if(newvalue==&quot;ALL&quot;)_sql.querytods(&quot;LHTML&quot;  , &quot;id=&quot;+newvalue );
			else _sql.querytods(&quot;GetSubClass&quot;  , &quot;id=&quot;+newvalue );
		}
		else {
			_sql.querytods(&quot;QueryLHTML&quot;  , &quot;str=&quot;+newvalue );

		}
		// 清空主数据区
		for (var i=6;i&lt;=_this.GetRowCount(1)-3;i++){
			_this.SetCellText(1,i,2,&quot;&quot;);
			_this.SetToStandardCell(1,i,2);
			_this.SetCellText(1,i,3,&quot;&quot;);
		}
		var nn = _this.XMLDS_GetRowCount()-(_this.GetRowCount(1)-3-6);

		for ( var i=0;i&lt;=nn;i++){
			_this.InsertRow(1,7);
		}
		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++){
			_this.SetCellText(1,6+i,3,_this.XMLDS_GetString(i,&quot;id&quot;)+&quot;.&quot;+_this.XMLDS_GetString(i,&quot;name&quot;) );
			_this.SetCellText(1,6+i,4,_this.XMLDS_GetString(i,&quot;id&quot;) );
			_this.SetCellText(1,6+i,5,_this.XMLDS_GetString(i,&quot;name&quot;) );
			_this.SetCellText(1,6+i,6,_this.XMLDS_GetString(i,&quot;previews&quot;) );

			var imgurl = _this.XMLDS_GetString(i,&quot;previews&quot;) ;

			if(imgurl!=&quot;&quot;){

				_this.LoadToImage(&quot;&quot;,1,6+i,2,imgurl);
			}
		}

	}

}

