				var id=_this.GetCellText(1,row,4);
				var name=_this.GetCellText(1,row,5);
				var imgurl  = _this.GetCellText(1,row,6);
				if(id!=&quot;&quot;)SelectClsid(id,name,imgurl  );
			}
		}
		else{
			var id=_this.GetCellText(1,row,4);
			var name=_this.GetCellText(1,row,5);
			var imgurl  = _this.GetCellText(1,row,6);
			if(id!=&quot;&quot;)SelectClsid(id,name,imgurl  );

		}
	}
}

