function SelectClsid(id,name,imgurl )
{
	_this.SetCellText(1,3,3,id);
	_this.SetCellText(1,3,4,name);
	if(imgurl !=&quot;&quot;)
		_this.LoadToImage(&quot;&quot;,1,4,3,imgurl);
	else _this.SetToStandardCell(1,4,2);

	if(_this.GetCellText(0,5,3)==&quot;&quot;){
		_this.SetCellText(0,5,3,id+&quot;-1&quot;);
		_this.SetCellText(0,5,6,name);
		_this.SetCellText(0,7,3,imgurl );


	}
	_this.SetCellText(0,27,3,id);
	titlemode_modify();
	 bodymode_modify();
}

