	}
}


//删除表
function  CreateTable(ustab,drotab,sql){
	var bo = &quot;成功&quot;;
	var db = null;
	try{
	db = new pubpack.EADatabase();
	var ds = db.QuerySQL(ustab);

	if(ds.getValueAt(0,0) &gt; 0){
		db.ExcecutSQL(drotab);
		db.Commit();
	}
	//创建表
	CreateLHMTL(sql);
	db.Commit();
	}catch ( ee ) {
		bo = &quot;失败&quot;;
		db.Rollback();
		throw new pubpack.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();	
	}
	return bo;
}

