CREATE OR REPLACE VIEW V_LSYSURLCLASS AS 
select distinct org orgid,refid id, name  from lsysurl where refid is not null  order by org,refid

 create table LSYSURL ( 
	GUID CHAR(32) default SYS_GUID()  not null ,
	ORG VARCHAR2(20),
	NAME VARCHAR2(256),
	NOTE VARCHAR2(1024),
	HTMLGUID VARCHAR2(32),
	URL VARCHAR2(256),
	ICON VARCHAR2(256),
	SEQID NUMBER(22),
	CRTDAT DATE default SYSDATE,
	REFID VARCHAR2(20),
	ID VARCHAR2(20),
	SUPPERID VARCHAR2(20),
	TARGET VARCHAR2(256),
	ICON2 VARCHAR2(256) );
  comment on column LSYSURL.GUID is &apos;内部ID&apos; ;
 comment on column LSYSURL.ORG is &apos;组织&apos; ;
 comment on column LSYSURL.NAME is &apos;名称&apos; ;
 comment on column LSYSURL.NOTE is &apos;备注&apos; ;
 comment on column LSYSURL.HTMLGUID is &apos;正文&apos; ;
 comment on column LSYSURL.URL is &apos;地址~比如entry.sp show.sp 或直接的URL地址&apos; ;
 comment on column LSYSURL.ICON is &apos;图片的地址&apos; ;
 comment on column LSYSURL.SEQID is &apos;排序号&apos; ;
 comment on column LSYSURL.CRTDAT is &apos;创建日期&apos; ;
 comment on column LSYSURL.REFID is &apos;栏目~如友好连接、产品介绍、项目介绍&apos; ;
 comment on column LSYSURL.ID is &apos;编号&apos; ;
 comment on column LSYSURL.SUPPERID is &apos;树状结构的上层编码&apos; ;
 comment on column LSYSURL.TARGET is &apos;&apos; ;
 comment on column LSYSURL.ICON2 is &apos;图标2的地址&apos; ;
