		GetObj(\&quot;List2\&quot;).innerHTML = GetObj(\&quot;List1\&quot;).innerHTML;
		GetObj(&apos;ISL_Cont&apos;).scrollLeft = fill;
		GetObj(\&quot;ISL_Cont\&quot;).onmouseover = function(){clearInterval(AutoPlayObj);}
		GetObj(\&quot;ISL_Cont\&quot;).onmouseout = function(){AutoPlay();}
		AutoPlay();
		function GetObj(objName){if(document.getElementById){return eval(&apos;document.getElementById(\&quot;&apos;+objName+&apos;\&quot;)&apos;)}else{return eval(&apos;document.all.&apos;+objName)}}
		function AutoPlay(){ //×Ô¶¯¹ö¶¯
		 clearInterval(AutoPlayObj);
		 AutoPlayObj = setInterval(&apos;ISL_GoDown();ISL_StopDown();&apos;,&quot;+time+&quot;); //¼ä¸ôÊ±¼ä
		}
		function ISL_GoUp(){ //ÉÏ·­¿ªÊ¼
		 if(MoveLock) return;
		 clearInterval(AutoPlayObj);
		 MoveLock = true;
		 MoveTimeObj = setInterval(&apos;ISL_ScrUp();&apos;,Speed); 
		} 
		function ISL_StopUp(){ //ÉÏ·­Í£Ö¹
		 clearInterval(MoveTimeObj); 
		 if(GetObj(&apos;ISL_Cont&apos;).scrollLeft % PageWidth - fill != 0){
		  Comp = fill - (GetObj(&apos;ISL_Cont&apos;).scrollLeft % PageWidth);
		  CompScr();
		 }else{
		  MoveLock = false;
		 }
		 AutoPlay();
		}
		function ISL_ScrUp(){ //ÉÏ·­¶¯×÷
		 if(GetObj(&apos;ISL_Cont&apos;).scrollLeft &lt;= 0){GetObj(&apos;ISL_Cont&apos;).scrollLeft = GetObj(&apos;ISL_Cont&apos;).scrollLeft + GetObj(&apos;List1&apos;).offsetWidth}
		 GetObj(&apos;ISL_Cont&apos;).scrollLeft -= Space ;
		}
		function ISL_GoDown(){ //ÏÂ·­
		 clearInterval(MoveTimeObj);
		 if(MoveLock) return; 
		 clearInterval(AutoPlayObj);
		 MoveLock = true;
		 ISL_ScrDown();
		 MoveTimeObj = setInterval(&apos;ISL_ScrDown()&apos;,Speed);
		}
		function ISL_StopDown(){ //ÏÂ·­Í£Ö¹
		 clearInterval(MoveTimeObj);
		 if(GetObj(&apos;ISL_Cont&apos;).scrollLeft % PageWidth - fill != 0 ){
		  Comp = PageWidth - GetObj(&apos;ISL_Cont&apos;).scrollLeft % PageWidth + fill;
		  CompScr();
		 }else{
		  MoveLock = false;
		 }
		 AutoPlay();
		}
		function ISL_ScrDown(){ //ÏÂ·­¶¯×÷
		 if(GetObj(&apos;ISL_Cont&apos;).scrollLeft &gt;= GetObj(&apos;List1&apos;).scrollWidth){GetObj(&apos;ISL_Cont&apos;).scrollLeft = GetObj(&apos;ISL_Cont&apos;).scrollLeft - GetObj(&apos;List1&apos;).scrollWidth;}
		 GetObj(&apos;ISL_Cont&apos;).scrollLeft += Space ;
		}
		function CompScr(){
		 var num;
		 if(Comp == 0){MoveLock = false;return;}
		 if(Comp &lt; 0){ //ÉÏ·­
		  if(Comp &lt; -Space){
		   Comp += Space;
		   num = Space;
		  }else{
		   num = -Comp;
		   Comp = 0;
		  }
		  GetObj(&apos;ISL_Cont&apos;).scrollLeft -= num;
		  setTimeout(&apos;CompScr()&apos;,Speed);
		 }else{ //ÏÂ·­
		  if(Comp &gt; Space){
		   Comp -= Space;
		   num = Space;
		  }else{
		   num = Comp;
		   Comp = 0;
		  }
		  GetObj(&apos;ISL_Cont&apos;).scrollLeft += num;
		  setTimeout(&apos;CompScr()&apos;,Speed);
		 }
		}
	&lt;/script&gt;&quot;;

	return html;
}

