//另存为
function SaveAS()
{
	var id=prompt(&quot;请输入新的ID号&quot;,&quot;&quot;);
	if(id!=undefined&&id!=&quot;&quot; ) {
		// 通过Get的方式调用服务端的一个函数
		var sParam =&quot;id=&quot;+id+&quot;&deforg=&quot;+G_DEFORG+&quot;&oldid=&quot;+_this.GetCellText(0,5,3);
		try {
			ret = invokeOSFunc(&quot;SaveAS&quot;,sParam) ;    
			if(ret!=&quot;&quot;&&confirm(&quot;复制成功，是否打开？&quot;)==1){
				openWindow(&quot;show.sp?grdid=LObject&guid=&quot;+ret);
			}   
		}
		catch ( e ) {
			alert ( e );
		}	
	}
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );


// 客户端param传入的参数可以直接使用
function SaveAS()
{
	var db = null;
	var guid = &quot;&quot;;
	try {
		db = new pubpack.EADatabase();	// 如果连接到其他数据库, new pubpack.EADatabase(“dbname”)
		guid = db.GetSQL(&quot;select sys_guid() guid from dual&quot;);
		var ds = db.QuerySQL(&quot;select COLUMN_NAME from user_tab_columns where table_name=&apos;LAYOBJ&apos;&quot;);
		var cols=&quot;&quot;;
		for ( var i=0;i&lt;ds.getRowCount();i++){
			var colname= ds.getStringAt(i,&quot;COLUMN_NAME&quot;);
			if(colname!=&quot;GUID&quot;&& colname!=&quot;ID&quot;){
				cols+=&quot;,&quot;+colname;	
			}
		}
		var sql =&quot;insert into LAYOBJ(guid,id&quot;+cols+&quot;) select &apos;&quot;+guid+&quot;&apos;,&apos;&quot;+id+&quot;&apos;&quot;+cols+&quot; from LAYOBJ where id=&apos;&quot;+oldid+&quot;&apos; and deforg=&apos;&quot;+deforg+&quot;&apos;&quot;;

		var retnum=db.ExcecutSQL(sql);

		db.Commit();	
	}
	catch ( ee ) {
		db.Rollback();
		throw new pubpack.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
	return guid ;
}

ROW.DATDSC:
CREATE OR REPLACE VIEW V_LSYSURLCLASS AS 
select distinct org orgid,refid id, name  from lsysurl where refid is not null  order by org,refid

 create table LSYSURL ( 
	GUID CHAR(32) default SYS_GUID()  not null ,
	ORG VARCHAR2(20),
	NAME VARCHAR2(256),
	NOTE VARCHAR2(1024),
	HTMLGUID VARCHAR2(32),
	URL VARCHAR2(256),
	ICON VARCHAR2(256),
	SEQID NUMBER(22),
	CRTDAT DATE default SYSDATE,
	REFID VARCHAR2(20),
	ID VARCHAR2(20),
	SUPPERID VARCHAR2(20),
	TARGET VARCHAR2(256),
	ICON2 VARCHAR2(256) );
  comment on column LSYSURL.GUID is &apos;内部ID&apos; ;
 comment on column LSYSURL.ORG is &apos;组织&apos; ;
 comment on column LSYSURL.NAME is &apos;名称&apos; ;
 comment on column LSYSURL.NOTE is &apos;备注&apos; ;
 comment on column LSYSURL.HTMLGUID is &apos;正文&apos; ;
 comment on column LSYSURL.URL is &apos;地址~比如entry.sp show.sp 或直接的URL地址&apos; ;
 comment on column LSYSURL.ICON is &apos;图片的地址&apos; ;
 comment on column LSYSURL.SEQID is &apos;排序号&apos; ;
 comment on column LSYSURL.CRTDAT is &apos;创建日期&apos; ;
 comment on column LSYSURL.REFID is &apos;栏目~如友好连接、产品介绍、项目介绍&apos; ;
 comment on column LSYSURL.ID is &apos;编号&apos; ;
 comment on column LSYSURL.SUPPERID is &apos;树状结构的上层编码&apos; ;
 comment on column LSYSURL.TARGET is &apos;&apos; ;
 comment on column LSYSURL.ICON2 is &apos;图标2的地址&apos; ;
