function _thisOnpost_loaddata(sheet)
{
       //queryonly = &quot;0&quot;;
       setButton();
       var fields = &quot;GUID,GUID,sys_guid();&quot;+
            &quot;id,编号,,,1;&quot;+
	    &quot;name,名称,,,1;&quot;+
	    &quot;refid,所属中间件,,,1;&quot;+
	    &quot;note,说明,,,1;&quot;;	    
      var where = &quot;&quot;;
      var orderby = &quot;ID&quot;;	// 字段的列表

      // 参数1：基表的名称
      // 参数2：字段列表 格式是通过;分隔不同的字段，每个分隔中使用,来分隔不同的字段属性
      //            字段属性： ID,NAME,默认值,正则表达式,是否列入查询的字段(=1是)
      // 参数3：主键
      // 参数4：显示空行标志 =true表示如果结果集为空，隐藏开头的两个空行,强制用户点击“新增”，以保证填入默认值
      // 参数5：查询的表名称，在某些情况下，查询的表名称和基表名称不一样，默认=&quot;&quot;表示查询表名称采用基表名称
      // 参数6：where条件，是不带where的子句如&quot;ID like &apos;01%&apos;&quot;
      // 参数7：orederby，是不带order by的子句如&quot;ID,REFID&quot;, 

      _dbhelper.InitSQL(&quot;moduletyp&quot;,fields,&quot;GUID&quot;,false,&quot;moduletyp&quot;,where,orderby);
      _dbhelper.InitColumnList(fields );
      _dbhelper.Load(1,1,2,2);

       // 自定义的特殊处理
       // 如：
       //       1、设置列的邦定属性
       //       2、设置默认值
       //       3、设置可修改或隐藏属性
}

