ROW.GRDJSDS_VALUE:
var G_RUNGRDID=&quot;&quot;;//如果传入这个参数，进程结束了以后打开目标中间件，并传入guid
var G_RUNGRDGUID=&quot;&quot;; //打开目标中间件传入的guid，如果为空，使用 jobSEQID
var G_RUNSPID = &quot;&quot;;//打开目标.sp传入的guid，如果为空，使用 jobSEQID

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);
	UpdateProcess();

	try{ G_RUNGRDID=rungrdid;}catch(e){}
	try{ G_RUNSPID=runspid;}catch(e){}
	try {G_RUNGRDGUID=rungrdguid;}catch(e){}
	if (G_RUNGRDGUID==&quot;&quot;) G_RUNGRDGUID = jobseqid;
	_this.SetRowVisible(0,7,0);
	_this.SetCellTopBorder(0,7,4,0);
}
var timer;
var cnt = 1;
function UpdateProcess()
{
	_this.SetRedraw(0);
	var stat = &quot;&quot;;
	_sql.querytods(&quot;MAIN&quot;,&quot;jobseqid=&quot;+jobseqid);
	_this.SetCellText(0,1,1,_this.XMLDS_GetString(0,&quot;NAME&quot;) );
	_this.SetCellText(0,3,2,_this.XMLDS_GetString(0,&quot;PERCENTNOTE&quot;) );
	var percent = 1.0*_this.XMLDS_GetString(0,&quot;PERCENT&quot;);
	_this.SetToRectbox(0,5,2, &quot;&quot;,&quot;&quot;,&quot;&quot;+(percent*(_this.GetColWidth(0,2)+_this.GetColWidth(0,3)+_this.GetColWidth(0,4))/100),&quot;&quot;+_this.GetRowHeight(0,5), &quot;#FF0000&quot;, 1,&quot;#FF0000&quot;,1,&quot;#FF0000&quot;,1,&quot;#FF0000&quot;,1,&quot;#FF0000&quot;);
//	_this.SetCellText(0,3,4,percent+&quot;%&quot; );
	_this.SetCellText(0,5,2,percent+&quot;%&quot; );
	stat  = _this.XMLDS_GetString(0,&quot;STAT&quot;);
	_this.SetCellText(0,8,4,stat);


	_this.SetCellText(0,10,4,cnt );

	cnt ++;
	_sql.querytods(&quot;DTL&quot;,&quot;jobseqid=&quot;+jobseqid);
	var str = &quot;&quot;;
	for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
		if ( _this.XMLDS_GetString(i,&quot;NAME&quot;)!=&quot;&quot; )
			str+=_this.XMLDS_GetString(i,&quot;NAME&quot;) + &quot;\r&quot;;
	}

	_this.SetCellText(0,9,2,str );

	_this.SetRedraw(1);
	if (stat  == &quot;end&quot; ){
		clearTimeout(timer);
		_sql.querytods(&quot;TIM&quot;,&quot;jobseqid=&quot;+jobseqid);

		_this.SetToCodeEditor(&quot;&quot;,0,9,2,str,&quot;&quot;,&quot;&quot;);

		if ( G_RUNSPID!= &quot;&quot; ) {
			_this.SetRowVisible(0,7,1);
			_this.SetRedraw(1);
//			if(confirm ( &quot;本进程后台执行时间为&quot;+ _this.XMLDS_GetString(0,&quot;NAME&quot;)+&quot;秒, 是否查看报告?&quot;)==1 ) {
//				openWindow( G_RUNSPID+&quot;.sp?guid=&quot;+G_RUNGRDGUID+&quot;&leftgrdid=&quot;+G_RUNGRDID );//leftgrdid是为了兼容chartframe.sp
//			}

		}
		else if ( G_RUNGRDID!= &quot;&quot; ) {
			_this.SetRowVisible(0,7,1);
			_this.SetRedraw(1);
//			if(confirm ( &quot;本进程后台执行时间为&quot;+ _this.XMLDS_GetString(0,&quot;NAME&quot;)+&quot;秒, 是否查看报告?&quot;)==1 ) {
//				openWindow( &quot;show.sp?grdid=&quot;+G_RUNGRDID+&quot;&guid=&quot;+G_RUNGRDGUID );
//			}

		}

		else alert ( &quot;本进程后台执行时间为&quot;+ _this.XMLDS_GetString(0,&quot;NAME&quot;)+&quot;秒&quot; );

	}
	else if (stat  == &quot;error&quot; ){
		alert ( &quot;提醒：本进程后运行有错误!&quot; );
		clearTimeout(timer);
	}
	else 
		timer = setTimeout(&apos;UpdateProcess()&apos;,1000);


}
//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if ( id == &quot;取消&quot; ) {
		if( confirm( &quot;取消该进程，是否继续？&quot; )== 1 ) {
			_sql.run(&quot;DEL&quot;,&quot;jobid=&quot;+jobseqid );
			window.close();
		}
	}
	if ( id == &quot;关闭&quot; ) {
		window.close();
	}
	if ( id ==&quot;打开分析表&quot;){
		if ( G_RUNSPID!= &quot;&quot; ) openWindow( G_RUNSPID+&quot;.sp?guid=&quot;+G_RUNGRDGUID+&quot;&leftgrdid=&quot;+G_RUNGRDID );//leftgrdid是为了兼容chartframe.sp
		else if ( G_RUNGRDID!= &quot;&quot; ) openWindow( &quot;show.sp?grdid=&quot;+G_RUNGRDID+&quot;&guid=&quot;+G_RUNGRDGUID );


	}
}
