ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);
	UpdateProcess();

}
var timer;
var cnt = 1;
function UpdateProcess()
{
	_this.SetRedraw(0);
	var stat = &quot;&quot;;
	for ( var i=1;i&lt;=_this.GetRowCount(0)-2;i++ ) {
		for ( var j=0;j&lt;_this.GetColCount(0);j++ ) 
			_this.SetCellText(0,i,j,&quot;&quot;);
		_this.SetToRectbox(0,i,4, &quot;&quot;,&quot;&quot;,&quot;0&quot;,0, &quot;#FFFFFF&quot;, 1,&quot;#FFFFFF&quot;,1,&quot;#FFFFFF&quot;,1,&quot;#FFFFFF&quot;,1,&quot;#FFFFFF&quot;);

	}
	var xml = _sql.query(&quot;MAIN&quot;,&quot;&quot;);
	_this.SetXmlDS(0,1,0,15,5,xml);

	for ( var i=1;i&lt;=_this.GetRowCount(0)-2;i++ ) {
		var percent = _this.GetCellText(0,i,4);
		if ( percent!= &quot;&quot; ){
			_this.SetToRectbox(0,i,4, &quot;&quot;,&quot;&quot;,&quot;&quot;+(percent*(_this.GetColWidth(0,4))/100),&quot;&quot;+_this.GetRowHeight(0,i), &quot;#FF0000&quot;, 1,&quot;#FF0000&quot;,1,&quot;#FF0000&quot;,1,&quot;#FF0000&quot;,1,&quot;#FF0000&quot;);
			_this.SetCellText(0,i,4,percent+&quot;%&quot;);
		}

	}
	_this.SetCellText(0,_this.GetRowCount(0)-1,4,cnt );
	timer = setTimeout(&apos;UpdateProcess()&apos;,1000);
	cnt ++;
	_this.SetRedraw(1);
}


//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if ( row &gt;=1 && row&lt;=_this.GetRowCount(0)-2 ) {
		var jobseqid = _this.GetCellText(0,row,0);
		if ( col == _this.GetColCount(0)-1) {
			if( confirm( &quot;取消该进程，是否继续？&quot; )== 1 ) {
				_sql.run(&quot;DEL&quot;,&quot;jobid=&quot;+jobseqid );
				UpdateProcess();
			}
		}
		else 
			window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=&quot;+jobseqid ,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );

	}
}
