var sheetflow = 0 ;
//分析流程图
function loadFlowSvg(typ)
{
	var svg = invokeOSFunc(&quot;loadFlowSvg&quot;,&quot;filename=&quot;+typ+&quot;.flow&syt=x&quot;);
	_this.SetToSVGForm(&quot;flow1&quot;,sheetflow,-1,-1,svg);
}


//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);
	loadFlowSvg(&quot;VAPP&quot;);
	_this.SVGFORM_SetTitleText(&quot;flow1&quot;,&quot;当前执行的虚拟化应用(&quot;+G_VAPPID+&quot;)&quot;+G_VAPPNAME,100);
	setTimeout(&quot;start1()&quot;,1000); //1.载入初始环境
}
function start1()
{
	//1.载入初始环境
	_this.SVGFORM_SetStatusText(&quot;flow1&quot;,&quot;正在载入初始环境&quot;,-1);

	_this.XMLCTL_SetAttribe(&quot;flow1&quot;,&quot;img_step1&quot;,&quot;width&quot;,&quot;58&quot;);
	_this.XMLCTL_SetAttribe(&quot;flow1&quot;,&quot;img_step1&quot;,&quot;height&quot;,&quot;58&quot;);
	//this.XMLCTL_SetValue(&quot;flow1&quot;,&quot;text_step1&quot;,&quot;执行中&quot;);
	LoadVAPP();
	_this.XMLCTL_SetValue(&quot;flow1&quot;,&quot;text_step1&quot;,&quot;载入初始环境OK&quot;);
	setTimeout(&quot;start2()&quot;,2000);  //1.载入初始环境

}
function start2()
{
	_this.XMLCTL_SetAttribe(&quot;flow1&quot;,&quot;img_step2&quot;,&quot;width&quot;,&quot;58&quot;);
	_this.XMLCTL_SetAttribe(&quot;flow1&quot;,&quot;img_step2&quot;,&quot;height&quot;,&quot;58&quot;);
	//this.XMLCTL_SetValue(&quot;flow1&quot;,&quot;text_step2&quot;,&quot;执行中&quot;);
	_this.SVGFORM_SetStatusText(&quot;flow1&quot;,&quot;正在检查环境&quot;,-1);

	_sql.querytods(&quot;QueryVAPPDTL&quot;, &quot;id=OnLoad&vappid=&quot;+G_VAPPID+&quot;&thissytid=&quot;+thissytid);	//OnOpenAPP, OnCloseAPP
	var retstr = &quot;&quot;;
	var errmsg = &quot;&quot;;
	if( _this.XMLDS_GetRowCount()&gt;0 ){
		var wsid = _this.XMLDS_GetString(0,&quot;WSID&quot;);
		if (wsid !=&quot;&quot; ) {
			_this.SVGFORM_SetStatusText(&quot;flow1&quot;,&quot;调用WebService(id=&quot;+wsid +&quot;)&quot; ,-1);
			var uguid = &quot;&quot;;
			var url1 = &quot;WEBS.sp?uguid=&quot;+uguid+&quot;&wsid=&quot;+wsid;
			retstr += _this.HttpCall(url1);
		}
		var etlid = _this.XMLDS_GetString(0,&quot;ETLID&quot;);
		if (etlid !=&quot;&quot; ) {
			_this.SVGFORM_SetStatusText(&quot;flow1&quot;,&quot;执行ETL(id=&quot;+etlid +&quot;)&quot; ,-1);
			var url1 = &quot;RunETL.sp?etlid=&quot;+etlid ;
			retstr += _this.HttpCall(url1);
		}
		var url = _this.XMLDS_GetString(0,&quot;CALLURL&quot;);
		if (url!=&quot;&quot; ) {
			_this.SVGFORM_SetStatusText(&quot;flow1&quot;,&quot;远程调用&quot;+url,-1);
			retstr += _this.HttpCall(url);
		}
		var osfunc = _this.XMLDS_GetString(0,&quot;OSFUNC&quot;);
		if (osfunc !=&quot;&quot; ) {
			_this.SVGFORM_SetStatusText(&quot;flow1&quot;,&quot;执行&quot;+osfunc ,-1);
			var url1 = thissytid+&quot;.VAPPOS.&quot;+vappid+&quot;_&quot;+osfunc+&quot;.osp?vappid=&quot;+vappid+&quot;&thissytid=&quot;+thissytid;

			var ss =  _this.HttpCall(url1);
			if ( ss.substring(0,5)==&quot;ERROR&quot;) {
				alert ( ss );
				errmsg = ss;
			}		
			retstr +=ss ;
			//retstr += _this.HttpCall(url);
		}


	}
	_this.XMLCTL_SetValue(&quot;flow1&quot;,&quot;text_step2&quot;,retstr +&quot;检查环境OK&quot;);

	if ( errmsg != &quot;&quot; ) return ;

	setTimeout(&quot;start3()&quot;,1000);  //1.载入初始环境


}
