ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(sheet,&quot;H&quot;,&quot;30%&quot;);

	//&mwid=ImpFile&sytid=SBCW
	_this.SetCellText(sheet,4,2,sytid);
	_this.SetCellText(sheet,5,2,mwid);


	var modtyp = _this.CreateListValue();
//	_this.SetListValue(modtyp,&quot;0&quot;,&quot;分类&quot;);
	_this.SetListValue(modtyp,&quot;1&quot;,&quot;页面权限&quot;);
	_this.SetListValue(modtyp,&quot;2&quot;,&quot;单据权限&quot;);
	_this.SetListValue(modtyp,&quot;3&quot;,&quot;脚本权限&quot;);

	_this.SetToComboBox(&quot;&quot;,sheet,10,2,modtyp);

	showBillAuth(-1);

	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetFixedRow(1,4);
	_this.SetFixedCol(1,1);
	loadSysMod();
	for (var r=4;r&lt;_this.GetRowCount(1);r++) {
		if (_this.GetCellText(1,r,2) != &quot;&quot;) {
			_this.SetToComboBox(&quot;&quot;,1,r,5,modtyp);
		}
	}
}

function showBillAuth(flag)
{
	for (var i=12;i&lt;=18;i++) {
		_this.SetRowVisible(0,i,flag);
		_this.SetToBoolBox(0,i,2);
	}
}
//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 0 && row == 10 && col == 2) {
		var modtyp = newvalue;
		if (modtyp == &quot;2&quot;) {
			showBillAuth(1);
			_this.SetRowVisible(sheet,11,-1);
		}
		else {
			showBillAuth(-1);
			_this.SetRowVisible(sheet,11,1);
		}
		if (modtyp == &quot;1&quot;) _this.SetCellText(sheet,11,1,&quot;动作（页面URL）：&quot;);
//		if (modtyp == &quot;2&quot;) _this.SetCellText(sheet,11,1,&quot;动作（页面URL）：&quot;);
		if (modtyp == &quot;3&quot;) _this.SetCellText(sheet,11,1,&quot;动作（OS函数名称）：&quot;);

	}
}


function loadSysMod()
{
	var xml = _sql.query(&quot;sysmod&quot;,&quot;thissytid=&quot;+sytid+&quot;&mwid=&quot;+mwid);
	_this.SetXmlDS(1,4,1,20,10,xml);
	_this.SetColVisible(1,1,-1);
}
//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (sheet == 0 && id == &quot;添加&quot;) {
		var param = new Object();
		param.sytid = sytid;
		param.mwid = mwid;
		param.refid = _this.GetCellText(sheet,6,2);
		param.mid = _this.GetCellText(sheet,7,2);
		param.mname = _this.GetCellText(sheet,8,2);
		param.note = _this.GetCellText(sheet,9,2);
		param.modtyp = _this.GetCellText(sheet,10,2);
		if (param.modtyp == &quot;2&quot;) {
			param.action = getBillAuths();
		}
		else {
			param.action = _this.GetCellText(sheet,11,2);
		}
		if (param.action != &quot;&quot;) {
			if (!confirm(&quot;确定添加该权限吗？&quot;)) return;
			var ret = invokeOSFuncExt(&quot;saveSysMod&quot;,param);
			alert(ret);
			window.location.reload();
		}
	}
}

function getBillAuths()
{
	var ret = &quot;&quot;;
	for (var i=12;i&lt;=18;i++) {
		var s = _this.GetCellText(0,i,2);
		if (s != &quot;&quot;) {
			var str = _this.GetCellText(0,i,3);
			var auth = (str.split(&quot;-&quot;)[0]).trim();
			if (ret == &quot;&quot;) ret += auth;
			else ret += &quot;,&quot; + auth;
		}
	}
	return ret;

}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	//删除权限
	if (sheet == 1 && col == 10) {
		if (!confirm(&quot;确定删除该权限吗？&quot;)) return;
		var ret = invokeOSFunc(&quot;deleteSysMod&quot;,&quot;guid=&quot;+_this.GetCellText(1,row,1));
		alert(ret);
		window.location.reload();
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function saveSysMod()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ps = db.prepareStatement(&quot;insert into sysmod (syt,id,name,modtyp,REFID,ACTION,GRDID,note) values (?,?,?,?,?,?,?,?)&quot;);
		ps.setString(1,sytid);
		ps.setString(2,mid);
		ps.setString(3,mname);
		ps.setString(4,modtyp);
		ps.setString(5,refid);
		ps.setString(7,mwid);
		ps.setString(8,note);
		if (modtyp == &quot;2&quot;) {
			var auths = action.split(&quot;,&quot;);
			for (var i=0;i&lt;auths.length();i++) {
				ps.setString(6,auths[i]);
			}
		}
		else {
			ps.setString(6,action);
		}
		ps.addBatch();
		ps.executeBatch();
		db.Commit();

		return &quot;添加权限成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function deleteSysMod()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;delete from sysmod where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);
		db.Commit();

		return &quot;删除权限成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
