function _thisOnpost_loaddata(sheet)
{
order = &quot; order by id&quot;;
       setButton();
       // 构造3个 SQL
       // 查询用户数据表记录
       selectSQL = &quot;select GUID,ID as 编号,REFID as 分类,NAME as 名称,ACTION as 动作,NOTE as 备注,&quot;+
                   &quot;MODTYP as 模块类型,GRDID as 中间件编号,SORTID as 排序号,SYT as 系统编号 &quot;+
                   &quot;from SYSMOD &quot;;
       //新增用户数据表记录
       insertSQL = &quot;INSERT INTO SYSMOD ( GUID,ID,REFID,NAME,ACTION,NOTE,MODTYP,GRDID,SORTID,SYT&quot;+
                   &quot; ) values( sys_guid(),?,?,?,?,?,?,?,?,? )&quot;;
       // 修改用户数据表记录
       updateSQL = &quot;UPDATE SYSMOD SET ID=?,REFID=?,NAME=?,ACTION=?,NOTE=?, &quot;+
                   &quot;MODTYP=?,GRDID=?,SORTID=?,SYT=?  &quot;+
                   &quot;where guid=?&quot;;
       //删除用户数据表记录
       deleteSQL = &quot;delete SYSMOD where guid=? &quot;;


       _dbhelper.SetSelectSQL( selectSQL );
       _dbhelper.SetInsertSQL( insertSQL );
       _dbhelper.SetUpdateSQL( updateSQL );
       _dbhelper.SetDeleteSQL( deleteSQL );

       // 载入数据并放在相应的位置
       _dbhelper.Load(1,1,2,2);

       // 自定义的特殊处理
       // 如：
       //       1、设置列的邦定属性
       //       2、设置默认值
       //       3、设置可修改或隐藏属性



}

