ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);

	for (var row = 1; row &lt; _this.GetRowCount(sheet); row ++) {
		_this.AutoFixOneRowHeight(sheet,row);
	}
	_this.AutoFixScreenWidth();

}

//新增
function New()
{
	var mwtyp = document.all(&quot;MWTYP&quot;).value;

	if ( mwtyp == &quot;&quot; ) {
		alert ( &quot;请先选择一个中间件类型&quot; );
	}
	var sytid = document.all(&quot;thissytid&quot;).value;

	openWindow(&quot;xlsgrid/jsp/design/designer.jsp?MWTYP=&quot;+mwtyp+&quot;&todo=new&selsytid=&quot;+sytid );
}

