//鼠标双击
function _thisOnMouseLClick(sheet,row,col)
{
	var mwtyp = _this.GetCellText(sheet,row,2);
	var mwid = _this.GetCellText(sheet,row,4);
	var sytid = _this.GetCellText(sheet,row,0);

	if(col==8&& _this.GetCellText(sheet,row,col)== &quot;工作流定义&quot; ) {

		openWindow(&quot;show.sp?grdid=flowOAMain&mwid=&quot;+mwid+&quot;&selsytid=&quot;+sytid );

	}
	else if(col==9&& _this.GetCellText(sheet,row,col)== &quot;中间件设计&quot; ) {

		window.location=&quot;xlsgrid/jsp/design/designer.jsp?MWTYP=&quot;+mwtyp+&quot;&grdid=&quot;+mwid+&quot;&sytid=&quot;+sytid ;


	}
	else if (sytid !=&quot;&quot; && mwid!=&quot;&quot; ){

	}	

}
