	}
	else if(col==9&& _this.GetCellText(sheet,row,col)== &quot;中间件设计&quot; ) {


	}
	else if (sytid !=&quot;&quot; && mwid!=&quot;&quot; ){
		openWindow(&quot;xlsgrid/jsp/design/designer.jsp?MWTYP=&quot;+mwtyp+&quot;&grdid=&quot;+mwid+&quot;&sytid=&quot;+sytid );
	}	
}

