ROW.DATDSC:
select b.id sytid,b.name sytnam,
c.id mwtyp,c.name mwtypname,a.id,a.name,a.note, a.typ 
,decode(c.id,&apos;F&apos;,&apos;工作流定义&apos;,&apos;B&apos;,&apos;工作流定义&apos;,&apos;&apos;) action
,&apos;中间件设计&apos; action2
from sysmw a, syt b,v_mwtyp c
where a.syt=b.id and a.mwtyp=c.id
and b.id like &apos;%[%thissytid]%&apos; and c.id like &apos;[%MWTYP]%&apos;
and upper(a.id) like &apos;%&apos;||upper(&apos;[%MWID]&apos;)||&apos;%&apos; and NVL(a.name,&apos; &apos;) like &apos;%[%MWNAME]%&apos;
and NVL(a.typ,&apos; &apos;) like &apos;[%MWSUBTYP]%&apos;
order by a.crtdat desc
