var IMAGEDIR = &quot;&quot;;//本地图片目录
var IMAGEFILESTR = &quot;&quot; ;// 本地图片目录中的文件，格式 filename,time~filename,time ...

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetToCodeEditor(&quot;body&quot;,sheet,-1,-1,&quot;&quot;,&quot;&quot;,&quot;&quot;);
	var title = &quot;正文编辑 - 请点击开始按钮的[使用HTML编辑器...]打开功能来编辑&quot; ;
	_this.SetToolbarString(title);
	_this.ShowToolBar(1);
	if ( _this.GetReleaseNo() &lt;&quot;6.98&quot; ) {
		alert ( &quot;请升级到6.98以上版本&quot; );

	}
	else 
		_this.SetWorkFile(SYSURLID);
	if ( urlguid==&quot;&quot; ) {
		_sql.querytods(&quot;GetURLGUID&quot;,&quot;id=&quot;+SYSURLID) ;

		urlguid =_this.XMLDS_GetString(0,&quot;GUID&quot;);


	}
	if (urlguid ==&quot;&quot;){
		alert ( &quot;没法找到该地址,id=&quot;+ SYSURLID);
	} 
	showPage(sheet);
	var workdir = _this.GetWorkDir();
	if( workdir == &quot;&quot; ) {
		alert ( &quot;在进行编辑之前，请首先设置您本地的工作目录&quot; );
		_this.SetWorkDir(&quot;&quot;);
		workdir = _this.GetWorkDir();
	}

	try{_this.CreateDirInWorkdir(&quot;images&quot; );}catch(e ) {}
	try{_this.CreateDirInWorkdir(&quot;images/&quot;+ deforg  );}catch(e ) {}

	try{_this.CreateDirInWorkdir(&quot;images/&quot;+ deforg  + &quot;/&quot;+SYSURLID );}catch(e ) {}
	IMAGEDIR  = workdir +&quot;/images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;



	//alert();
	_this.GetDirFileList(IMAGEDIR  );
	var str = &quot;&quot;;
	for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
		// 本地图片目录中的文件，格式 filename,time~filename,time ..
		if ( i!= 0 ) str += &quot;~&quot;;
		str += _this.XMLDS_GetString(i,&quot;FILENAME&quot; )+&quot;,&quot;+_this.XMLDS_GetString(i,&quot;TIME&quot; )+&quot;~&quot;;
	}
	IMAGEFILESTR  = str;

}

function showPage(sheet)
{
	var ret = &quot;&quot;;
	try {
		ret = _this.invokeOSFunc(&quot;getBlob&quot;,&quot;urlguid=&quot;+urlguid);
		_this.SetCellText(sheet,-1,-1,ret);
	}
	catch(e) {
		alert(&quot;初始化显示页面出错 &quot;+e.toString());
	}
}

