function SAVE()
{
	var param = new Object();
	param.urlguid = urlguid;
	param.body = _this.GetCellText(0,-1,-1);

	try {
		_this.invokeOSFuncExt(&quot;save&quot;,param);
		// 检查本地图片目录是否有新的图片或修改的图片
		_this.GetDirFileList(IMAGEDIR  );
		var str = &quot;&quot;;
		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
			// 本地图片目录中的文件，格式 filename,time~filename,time ..
			if ( i!= 0 ) str += &quot;~&quot;;
			str += _this.XMLDS_GetString(i,&quot;FILENAME&quot; )+&quot;,&quot;+_this.XMLDS_GetString(i,&quot;TIME&quot; )+&quot;~&quot;;
		}
		if (IMAGEFILESTR  != str ){
			if ( confirm( &quot;系统检测到本地图片目录有更新的文件，是否上传？&quot; )== 1 ) {
				_UploadImage(false );

			}
			IMAGEFILESTR = str;
		}


		alert(&quot;保存成功!&quot;);
	}
	catch(e) {
		alert(&quot;保存失败! &quot;+e);
	}

}


//图片资源下载
function DownloadImage()
{
	var workdir = _this.GetWorkDir();

	var xml = invokeOSFunc(&quot;GetWebresourceXMLDS&quot;,&quot;sysurlid=&quot;+SYSURLID+&quot;&deforg=&quot;+deforg);

	_this.XMLDS_Parse(xml);

	var dir = workdir +&quot;/images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;
	var webdir = &quot;images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;

	var n = _this.XMLDS_GetRowCount();
	var msg =&quot;下载服务器的图片文件到本地目录（&quot;+dir+&quot;）\n共&quot;+n+&quot;个文件，是否继续?&quot;;  
	if( n == 0 ) {
		alert (&quot;远程目录（&quot;+webdir +&quot;）没有需要下载的文件&quot;);  
		return ;
	}
	var files = &quot;&quot;;
	if ( confirm(msg) == 1 ) {
		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
			var filename = _this.XMLDS_GetString(i,&quot;FILENAME&quot; );
			// 下载

			files+=_this.DownLoadFileToWorkDir(webdir + filename ,webdir+filename ) + &quot;\n&quot;;

		}
		if ( files!= &quot;&quot; )
			alert ( &quot;已下载文件\n&quot;+ files );

	}

}

//图片资源Upload
function UploadImage()
{
	_UploadImage(true );
}
function _UploadImage(bPrompt)
{
	var workdir = _this.GetWorkDir();
	var dir = workdir +&quot;/images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;
	var webdir = &quot;webresource/images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;

	var n = _this.GetDirFileList(dir);
	var msg =&quot;把本地目录的图片文件（&quot;+dir+&quot;）同步到服务器\n共&quot;+n+&quot;个文件，是否继续?&quot;;  
	if ( n == -1 ) {
		alert ( &quot;本地的资源目录还没有建立，路径是：&quot; + dir );
		return ;
	}
	else if( n == 0 ) {
		alert (&quot;本地目录（&quot;+dir+&quot;）没有需要同步上传的文件&quot;);  
		return ;
	}

	if ( bPrompt==false || confirm(msg) == 1 ) {
		var files = &quot;&quot;;

		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
			var filename = _this.XMLDS_GetString(i,&quot;FILENAME&quot;);
			// 上传文件到xlsGridData
			var saveUrl = homeurl+&quot;UploadToXlsGridData.sp&quot;;
			_this.OpenHttpRequest(saveUrl,&quot;UPLOAD&quot;);
			_this.AddHttpRequestParam(&quot;uploadfile&quot;,dir + filename,1);//本地文件的路径
			_this.AddHttpRequestParam(&quot;path&quot;,webdir ,0);	//远程保存的目录
			_this.AddHttpRequestParam(&quot;filename&quot;,filename,0);//注意这里只能是文件名，用于远程保存的文件名
			_this.AddHttpRequestParam(&quot;handle&quot;,&quot;REPORTSTORE&quot;,0);
			var ret=_this.SendHttpRequest(); 
			_this.CloseHttpRequest();



			var loc1 = ret.indexOf( &quot;&lt;TOPIC&quot; );
			var loc2 = ret.indexOf( &quot;&lt;/TOPIC&quot; );
			var str = ret.substring(loc1+7,loc2);
			files+=&quot;&quot;+(i+1)+&quot;.&quot;+dir +filename+&quot;:&quot;+str +&quot;\n&quot;;
		}
		// 从xlsGridData同步到web
		try {
			invokeOSFunc(&quot;copytoweb&quot;,&quot;deforg=&quot;+deforg  +&quot;&sysurlid=&quot;+SYSURLID) ;       
		}
		catch ( e ) {
			alert ( e );
		}
		if ( files!= &quot;&quot; ) {
			alert ( &quot;上传文件情况：\n&quot;+ files );
		}


	}

}
