//导入HTML文件
function Impfile()
{
	var webdir = &quot;webresource/images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;
	var workdir = _this.GetWorkDir();
	var localdir = &quot;images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; ;

	var btn=document.getElementById(&quot;myfile&quot;);

	btn.click();
	var file = btn.value;
	var filepos = file.lastIndexOf ( &quot;\\&quot; );

	var dir = file.substr( 0,filepos+1 );

	var htmlstr = _this.LoadTextFile(file);

	var pos = -1;
	var pos1 = -1;
	var pos2 = -1;
	var posend = -1;
	var bUpload = 0;
	while ( 1&gt;0 ) {

		pos = htmlstr.indexOf(&quot;&lt;img&quot;,pos+1);
		if ( pos&lt;0 ) break;
		posend = htmlstr.indexOf(&quot;&gt;&quot;,pos+1);
		if ( posend&lt;0 ) break;

		pos1 = htmlstr.indexOf(&quot;src=\&quot;&quot;,pos+1);
		var yhflag = 1;//是否双引号
		if ( pos1&lt;0 || pos1&gt;= posend ) {
			pos1 = htmlstr.indexOf(&quot;src=&apos;&quot;,pos+1);
			yhflag = 0;
		}
		if ( pos1 &gt;0 && pos1&lt; posend  ) {
			if ( yhflag ==0 )
				pos2 = htmlstr.indexOf(&quot;&apos;&quot;,pos1+6);

			else pos2 = htmlstr.indexOf(&quot;\&quot;&quot;,pos1+6);

			if ( pos2 &gt;0 && pos2&lt; posend  ) {
				var filename =htmlstr.substr(pos1+5,pos2-pos1-5) ;
				var sfilename = filename.split( &quot;/&quot; );

				window.status = &quot;正在上传&quot;+dir + filename ;
				// 上传文件到xlsGridData
				var saveUrl = homeurl+&quot;UploadToXlsGridData.sp&quot;;
				_this.OpenHttpRequest(saveUrl,&quot;UPLOAD&quot;);
				_this.AddHttpRequestParam(&quot;uploadfile&quot;, dir + filename ,1);//本地文件的路径
				_this.AddHttpRequestParam(&quot;path&quot;,webdir ,0);	//远程保存的目录
				_this.AddHttpRequestParam(&quot;filename&quot;,sfilename[sfilename.length-1],0);//注意这里只能是文件名，用于远程保存的文件名
				_this.AddHttpRequestParam(&quot;handle&quot;,&quot;REPORTSTORE&quot;,0);
				var ret=_this.SendHttpRequest(); 
				_this.CloseHttpRequest();

				//复制图片到工作目录

				_this.DownLoadFileToWorkDir(dir + filename ,&quot;/images/&quot;+ deforg  + &quot;/&quot;+SYSURLID+&quot;/&quot; +sfilename[sfilename.length-1]) ;
				bUpload  = 1;
				// 替代src的内容
				htmlstr = htmlstr.substr( 0,pos1+5 ) +localdir +sfilename[sfilename.length-1]+ htmlstr.substr(pos2);
				var f1 = localdir +sfilename[sfilename.length-1];
				var f2 = filename ;
				posend += f1.length-f2.length; 
			}
		}
		pos = posend;

	}
	if ( bUpload  ==1 ) {
		// 从xlsGridData同步到web
		try {
			invokeOSFunc(&quot;copytoweb&quot;,&quot;deforg=&quot;+deforg  +&quot;&sysurlid=&quot;+SYSURLID) ;       
		}
		catch ( e ) {
			alert ( e );
		}
	}
	_this.SetRedraw(0);
	_this.SetCellText(0,-1,-1,htmlstr );
	_this.SetRedraw(1);	
}

