ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetCellText(0,6,6,usrorgid );
	_this.SetCellText(0,6,2,usrid );

	_sql.querytods(&quot;Query&quot;, &quot;ORGID=&quot;+usrorgid+&quot;&USRID=&quot;+usrid );
	if ( _this.XMLDS_GetRowCount() &gt; 0 ) {
		_this.SetCellText(0,10,1,&quot;该用户已授权，分配码是&quot;+_this.XMLDS_GetString(0,&quot;GUID&quot; ) );
		_this.SetCellText(0,2,2,_this.XMLDS_GetString(0,&quot;GUID&quot; ) );

		_this.SetCellText(0,4,2,_this.XMLDS_GetString(0,&quot;ORGID&quot; ) );
		_this.SetCellText(0,4,4,_this.XMLDS_GetString(0,&quot;SYTID&quot; ) );
		_this.SetCellText(0,4,6,_this.XMLDS_GetString(0,&quot;ACCID&quot; ) );

		_this.SetCellText(0,6,2,_this.XMLDS_GetString(0,&quot;USRID&quot; ) );
		_this.SetCellText(0,6,4,_this.XMLDS_GetString(0,&quot;USRNAM&quot; ) );
		_this.SetCellText(0,6,6,_this.XMLDS_GetString(0,&quot;USRORG&quot; ) );						

		_this.SetCellText(0,8,2,_this.XMLDS_GetString(0,&quot;STARTDAT&quot; ) );
		_this.SetCellText(0,8,4,_this.XMLDS_GetString(0,&quot;ENDDAT&quot; ) );
		_this.SetCellText(0,8,6,_this.XMLDS_GetString(0,&quot;USEFLG&quot; ) );		
	}

	else {
		_this.SetCellText(0,10,1,&quot;该用户还没有授权&quot;);
		_sql.querytods(&quot;QueryUSR&quot;, &quot;ORGID=&quot;+usrorgid+&quot;&USRID=&quot;+usrid );
		if ( _this.XMLDS_GetRowCount() &gt; 0 ) {
			_this.SetCellText(0,4,2,G_ORGID );
			_this.SetCellText(0,4,4,G_SYTID );
			_this.SetCellText(0,4,6,G_ACCID );
			_this.SetCellText(0,6,2,usrid );
			_this.SetCellText(0,6,6,_this.XMLDS_GetString(0,&quot;ORG&quot; ) );
			_this.SetCellText(0,6,4,_this.XMLDS_GetString(0,&quot;NAME&quot; ) );

			_this.SetCellText(0,8,2,G_LOGDAT );
			_this.SetCellText(0,8,4,&quot;&quot; );
			_this.SetCellText(0,8,6,&quot;1&quot; );


//			GUID,USRGUID,SYTID,ACCID,ORGID,STARTDAT,ENDDAT,CRTUSRNAM,USEFLG
			refreshGUID();
		}
		else {
			alert ( &quot;用户没有找到！&quot; );
			_this.SetCellText(0,10,1,&quot;用户没有找到！&quot;);
		}
	}
}

function refreshGUID()
{
	_this.SetCellText(0,2,2,invokeOSFunc(&quot;GetGUID&quot;,&quot;&quot;) );
}
//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if( id == &quot;重新分配&quot; ) {

		refreshGUID();
	}
	else if ( id == &quot;保存&quot; ) {
		//POST方式调用网络OS函数
		var param = new Object();
		//param.xmlstr = _this.GetXmlHaveCellID(0);
		param.guid = _this.GetCellText(0,2,2);
		param.acc = _this.GetCellText(0,4,6);
		param.syt = _this.GetCellText(0,4,2);
		param.org = _this.GetCellText(0,4,4);
		param.usrid = _this.GetCellText(0,6,2);
		param.usrnam = _this.GetCellText(0,6,4);
		param.usrorg = _this.GetCellText(0,6,6);
		param.startdat = _this.GetCellText(0,8,2);
		param.enddat = _this.GetCellText(0,8,4);
		param.useflg = _this.GetCellText(0,8,6);
		param.crtusrnam = G_USRNAM;

		try {
			msg = invokeOSFuncExt(&quot;save&quot;,param);
			alert ( msg );
		}
		catch (e) {
			alert ( e );
		}

	}
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );

// 客户端param传入的参数可以直接使用
function GetGUID()
{
	return pubpack.EADbTool.GetSQL( &quot;select sys_guid() from dual &quot; );
}

var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );

// 客户端param传入的参数可以直接使用
function save()
{
	var db = null;
	var msg= &quot;操作失败&quot;;

	try {
		db = new pubpack.EADatabase();	// 如果连接到其他数据库, new pubpack.EADatabase(“dbname”)
		var usrguid  =db.GetSQL(&quot;select guid from usr where id=&apos;&quot;+usrid+&quot;&apos; and org=&apos;&quot;+usrorg+&quot;&apos;&quot; );
		db.ExcecutSQL(&quot;delete from usracc where usrguid=&apos;&quot;+usrguid+&quot;&apos;&quot; );
		db.ExcecutSQL(&quot;insert into usracc(GUID,USRGUID,SYTID,ACCID,ORGID,STARTDAT,ENDDAT,CRTUSRNAM,USEFLG) &quot; + 
			&quot;values(&apos;&quot;+guid+&quot;&apos;, &apos;&quot;+usrguid+&quot;&apos;,&apos;&quot;+syt+&quot;&apos;,&apos;&quot;+acc+&quot;&apos;,&apos;&quot;+org+&quot;&apos;,to_date(&apos;&quot;+startdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),to_date(nvl(&apos;&quot;+enddat+&quot;&apos;,to_char(sysdate+3650,&apos;YYYY-MM-DD&apos;)),&apos;yyyy-mm-dd&apos;),&apos;&quot;+crtusrnam +&quot;&apos;,&apos;&quot;+useflg+&quot;&apos;) &quot;);
		db.Commit();
		msg= &quot;操作成功&quot;;

	}	
	catch ( ee ) {
		db.Rollback();
		throw new pubpack.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
	return msg;
}
