ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	var refidList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_SYSMODTYP&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var modtypList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_MODTYP&quot;),&quot;ID&quot;,&quot;NAME&quot;);

	for( var r=3;r&lt;=_this.GetMainCellRangeRow2(0);r++){
		if( _this.GetCellText(0,r,1)!=&quot;&quot;) {
			_this.SetToBoolBox(0,r,1);
			if (r != 3) {
				_this.SetToComboBox(&quot;&quot;,0,r,6,refidList);
				_this.SetToComboBox(&quot;&quot;,0,r,7,modtypList);
			}
		}
	}
	_this.SetColVisible(0,2,0);
	_this.SetRowVisible(0,0,-1);
	_this.SetRowVisible(0,1,-1);
	_this.SetRowVisible(0,2,-1);
	_this.AutoFixScreenWidth();

}
//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if ( sheet==0&& row==3&&col==1){
		var checkid=_this.GetCellText(0,3,1);
		for( var r=4;r&lt;_this.GetRowCount(0)-1;r++){
		if( _this.GetCellText(0,r,1)!=&quot;&quot;) 
			_this.SetCellText(0,r,1,checkid);		
		}
	}  
}
//保存
function save()
{
	var param = new Object();
	param.xmlstr = _this.GetXml(0,_this.GetMainCellRangeRow1(0),_this.GetMainCellRangeCol1(0)
		,_this.GetMainCellRangeRow2(0),_this.GetMainCellRangeCol1(0)+2 );
	param.thisrolguid= thisrolguid;

	try {
		msg = invokeOSFuncExt(&quot;save&quot;,param);
		alert ( msg );
	}
	catch (e) {
		alert ( e );
	}

}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if ( id==&quot;保存&quot;)
		save();  
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );

// 客户端param传入的参数可以直接使用
function save()
{
	var db = null;
	var msg= &quot;&quot;;

	try {
		db = new pubpack.EADatabase();	// 如果连接到其他数据库, new pubpack.EADatabase(“dbname”)
		try{db.ExcecutSQL(&quot;create view V_SYSMODTYP as select distinct refid id ,syt,refid name from sysmod &quot;);}catch(e){}
		var ds = new pubpack.EAXmlDS(xmlstr);	// 客户端可以传入一个xml
		//db.ExcecutSQL(&quot;delete from rolmod where rol =&apos;&quot;+thisrolguid+&quot;&apos;&quot;);
		for ( var i=0;i&lt;ds.getRowCount();i++){
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			var checkid = ds.getStringAt(i,&quot;CHECKID&quot;);
			db.ExcecutSQL(&quot;delete from rolmod where rol =&apos;&quot;+thisrolguid+&quot;&apos; and sysmod=&apos;&quot;+guid+&quot;&apos;&quot;);
			if ( checkid==&quot;1&quot;) {
				db.ExcecutSQL(&quot;insert into rolmod(sysmod,rol) values(&apos;&quot;+guid+&quot;&apos;,&apos;&quot;+thisrolguid+&quot;&apos;) &quot;);
			}
		}
		db.Commit();

		msg=&quot;操作成功&quot;;	
	}
	catch ( ee ) {
		db.Rollback();
		throw new pubpack.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
	return msg;

}

ROW.DATDSC:
select case when a.sysmod is null then &apos;0&apos; else &apos;1&apos; end  checkid,
b.guid,b.id,b.name,b.note,b.refid,b.modtyp,b.grdid,b.action from 
(select b.sysmod
from rol a, rolmod b
where a.guid=b.rol and a.guid=&apos;[%thisrolguid]&apos; and decode(nvl(a.org,&apos;&apos;),&apos;&apos;,&apos;[%SYS_ORGID]&apos;,a.org)=NVL(&apos;[%thisorgid]&apos;,&apos;[%SYS_ORGID]&apos;)
) a, sysmod b
where a.sysmod(+)=b.guid and b.syt=(select syt from rol where guid=&apos;[%thisrolguid]&apos;) 
and b.id like &apos;%[%thismodid]%&apos; and b.name like &apos;%[%MODNAM]%&apos;
and NVL(b.refid,&apos;&apos;) like &apos;[%refid]%&apos; 
and NVL(b.modtyp,&apos;&apos;) like &apos;[%modtyp]%&apos;
order by b.refid,b.id
