ROW.GRDJSDS_VALUE:
var listSubsytType;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(0,&quot;H&quot;,&quot;20%&quot;);
	_this.SplitSheet(1,&quot;H&quot;,&quot;50%&quot;);
	//_this.SplitSheet(2,&quot;V&quot;,&quot;40%&quot;);

	//_this.SetColVisible(1,0,-1);
	_this.SetColVisible(1,2,-1);
	//_this.SetColVisible(2,0,-1);
	_this.SetColVisible(2,2,-1);
	_this.SetColVisible(1,5,-1);
	_this.SetColVisible(2,5,-1);

	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	_sql.querytods(&quot;ROL&quot;,&quot;ROLGUID=&quot;+thisrolguid);
	_this.SetCellText(0,3,2,_this.XMLDS_GetString(0,&quot;ID&quot;));
	_this.SetCellText(0,5,2,_this.XMLDS_GetString(0,&quot;NAME&quot;));
	_this.SetCellText(0,7,2,_this.XMLDS_GetString(0,&quot;NOTE&quot;));

	listSubsytType = _this.CreateListValue();
	_this.SetListValue(listSubsytType ,&quot;ALL&quot;,&quot;全部&quot;);
	_this.SetListValue(listSubsytType ,&quot;PC&quot;,&quot;浏览器支持控件&quot;);
	_this.SetListValue(listSubsytType ,&quot;PCHTML&quot;,&quot;纯HTML格式&quot;);
	_this.SetListValue(listSubsytType ,&quot;PDA&quot;,&quot;PDA手机&quot;);
	_this.SetListValue(listSubsytType ,&quot;WAP&quot;,&quot;WAP手机&quot;);
	_this.SetListValue(listSubsytType ,&quot;J2ME&quot;,&quot;J2ME手机&quot;);

	setSubSytMenu(G_SYTID);

	_this.AutoFixScreenWidth();

	_this.SetColName(1,1,&quot;SUBID&quot;);
	_this.SetColName(1,2,&quot;TYP&quot;);
	_this.SetColName(1,3,&quot;NAME&quot;);
	_this.SetColName(1,4,&quot;AUTH&quot;);
	_this.SetColName(1,5,&quot;SHOW&quot;);

	_this.SetColName(2,0,&quot;ORDER&quot;);
	_this.SetColName(2,1,&quot;SUBID&quot;);
	_this.SetColName(2,2,&quot;TYP&quot;);
	_this.SetColName(2,3,&quot;NAME&quot;);
	_this.SetColName(2,4,&quot;AUTH&quot;);
	_this.SetColName(2,5,&quot;SHOW&quot;);
}


function setSubSytMenu(sytid)
{
	//加载授权信息
	var rolsubxml = _sql.query(&quot;ROLSUBSYT&quot;,&quot;ROLGUID=&quot;+thisrolguid+&quot;&SYTID=&quot;+G_SYTID);
	_this.SetXmlDS(3,0,0,4,5,rolsubxml);

	var ret = invokeOSFunc(&quot;load&quot;,&quot;mysytid=&quot;+sytid) ;
	_this.XMLDS_Parse(ret);
	var numsubsyt= 3;

	var numsubsheet = 0;
	for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
		//subid	typ	order	name	TITLE	ICON	URL
		var subid = _this.XMLDS_GetString(i,&quot;subid&quot;);
		var typ= _this.XMLDS_GetString(i,&quot;typ&quot;);
		var order= _this.XMLDS_GetString(i,&quot;order&quot;);
		var name= _this.XMLDS_GetString(i,&quot;name&quot;);
		if(order == &quot;0&quot; ) {
			numsubsyt ++;
			if (numsubsyt &gt; 3+15 ) {
				_this.AppendRow(1,_this.GetRowCount(1)-1 );
			}
			_this.SetToComboBox(&quot;&quot;,1,numsubsyt,2,listSubsytType );
			_this.SetCellText(1,numsubsyt,1,subid );
			_this.SetCellText(1,numsubsyt,2,typ);
			_this.SetCellText(1,numsubsyt,3,name);
			_this.SetToBoolBox(1,numsubsyt,4);
			_this.SetToBoolBox(1,numsubsyt,5);

			for (var k=0;k&lt;_this.GetRowCount(3);k++) {
				if (subid == _this.GetCellText(3,k,2) && _this.GetCellText(3,k,5) == &quot;0&quot;) {
					_this.SetCellText(1,numsubsyt,4,_this.GetCellText(3,k,3));
					_this.SetCellText(1,numsubsyt,5,_this.GetCellText(3,k,4));
				}
			}
		}
	}	
}
//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 1) {
		var cursubid = _this.GetCellText(sheet,row,1);
		var cursubnam = _this.GetCellText(sheet,row,3);
		_this.SetCellText(2,3,3,cursubid+&quot;-&quot;+cursubnam);

		_this.SetRowCount(2,9);
		for (var r=6;r&lt;9;r++) {
			for (var c=0;c&lt;_this.GetColCount(2);c++) {
				_this.SetCellText(2,r,c,&quot;&quot;);
			}
		}

		var ret = invokeOSFunc(&quot;load&quot;,&quot;mysytid=&quot;+sytid) ;
		_this.XMLDS_Parse(ret);
		var numsubsyt= 5;
		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
			//subid	typ	order	name	TITLE	ICON	URL
			var subid = _this.XMLDS_GetString(i,&quot;subid&quot;);
			var typ= _this.XMLDS_GetString(i,&quot;typ&quot;);
			var order= _this.XMLDS_GetString(i,&quot;order&quot;);
			var name= _this.XMLDS_GetString(i,&quot;name&quot;);
			if(order != &quot;0&quot; && subid == cursubid) {
				numsubsyt ++;
				if (numsubsyt &gt; 3+5 ) {
					_this.AppendRow(2,_this.GetRowCount(2)-1 );
				}
				_this.SetToComboBox(&quot;&quot;,2,numsubsyt,2,listSubsytType );
				_this.SetCellText(2,numsubsyt,0,order );
				_this.SetCellText(2,numsubsyt,1,subid );
				_this.SetCellText(2,numsubsyt,2,typ);
				_this.SetCellText(2,numsubsyt,3,name);
				_this.SetToBoolBox(2,numsubsyt,4);
				_this.SetToBoolBox(2,numsubsyt,5);

				for (var k=0;k&lt;_this.GetRowCount(3);k++) {
					if (subid == _this.GetCellText(3,k,2) && _this.GetCellText(3,k,5) == order) {
						_this.SetCellText(2,numsubsyt,4,_this.GetCellText(3,k,3));
						_this.SetCellText(2,numsubsyt,5,_this.GetCellText(3,k,4));
					}
				}
			}
		}
	}
}

//保存
function save()
{
	if (!confirm(&quot;保存当前设置，是否确定？&quot;)) return;
	try {
		var param = new Object();
		param.subsytxml = _this.GetXml(1,4,1,_this.GetRowCount(1)-1,5);
		param.subitemxml = _this.GetXml(2,6,0,_this.GetRowCount(2)-1,5);
		param.thissytid = G_SYTID;
		param.rolguid = thisrolguid;

		var ret = invokeOSFuncExt(&quot;save&quot;,param);
		alert(ret);

	}
	catch(e) {
		alert(&quot;保存失败!&quot;+e);
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if((sheet == 1 && row==1 && col==4 )||(sheet == 2 && row==3 &&col==4)){
		for (var i=row+3;i&lt;_this.GetRowCount(sheet);i++){
			if(_this.GetCellText(sheet,i,1)!=&quot;&quot; )_this.SetCellText(sheet,i,4,newvalue );
		}
	}
}

ROW.GRDOSDS_VALUE:
var xmldbpack = new JavaPackage(&quot;com.xlsgrid.net.xmldb&quot;);
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function load( ) { 
	var ds0 = xmldbpack.EAXmlDB.getSubSytDB(mysytid);
	if( ds0 == null )
		return pub.EAFunc.readFile(pub.EAOption.dynaDataRoot+&quot;/xmldb/sytdb/nullsubsytdb.xml&quot;);

	//if ( ds0!=null && ds0.GetRowCount()==0 )
	//	return pub.EAFunc.readFile(pub.EAOption.dynaDataRoot+&quot;/xmldb/sytdb/nullsubsytdb.xml&quot;);

	return ds0.GetXml();
}  

function save( ) { 
//	pub.EAFunc.WriteToFile(pub.EAOption.dynaDataRoot+&quot;/&quot;+mysytid+&quot;/subsytdb.xml&quot;,xml);
//	return &quot;保存成功&quot; ;

	var db = null;
	var sql = &quot;&quot;;

	try {
		db = new pub.EADatabase();

		var subds = null;
		var itmds = null;		
		if (subsytxml != &quot;&quot;) subds = new pub.EAXmlDS(subsytxml);
		if (subitemxml != &quot;&quot;) itmds = new pub.EAXmlDS(subitemxml);

		if (subds != null) {
			sql = &quot;delete from rolsubsyt where rol=&apos;&quot;+rolguid+&quot;&apos; and syt=&apos;&quot;+thissytid+&quot;&apos; and ord=&apos;0&apos;&quot;;
			db.ExcecutSQL(sql);
			for (var i=0;i&lt;subds.getRowCount();i++) {
				var subid = subds.getStringAt(i,&quot;SUBID&quot;);
				var auth = subds.getStringAt(i,&quot;AUTH&quot;);
				var show = subds.getStringAt(i,&quot;SHOW&quot;);
				if (subid != &quot;&quot; && auth == &quot;1&quot;) {
					sql = &quot;insert into rolsubsyt(rol,syt,subsytid,show,ord,auth) values (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;0&apos;,&apos;%s&apos;)&quot;.format([rolguid,thissytid,subid,show,auth]);
					db.ExcecutSQL(sql);
				}
			}
		}

		if (itmds != null) {
			if (itmds.getRowCount() &gt; 0) {
				var subsytid = itmds.getStringAt(0,&quot;SUBID&quot;);
				sql = &quot;delete from rolsubsyt where rol=&apos;&quot;+rolguid+&quot;&apos; and syt=&apos;&quot;+thissytid+&quot;&apos; and ord!=&apos;0&apos; and subsytid=&apos;&quot;+subsytid+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);
			}
			for (var i=0;i&lt;itmds.getRowCount();i++) {
				var subid = itmds.getStringAt(i,&quot;SUBID&quot;);
				var auth = itmds.getStringAt(i,&quot;AUTH&quot;);
				var show = itmds.getStringAt(i,&quot;SHOW&quot;);
				var ord = itmds.getStringAt(i,&quot;ORDER&quot;);
				if (subid != &quot;&quot; && auth == &quot;1&quot;) {
					sql = &quot;insert into rolsubsyt(rol,syt,subsytid,show,ord,auth) values (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([rolguid,thissytid,subid,show,ord,auth]);
					db.ExcecutSQL(sql);
				}
			}
		}

		db.Commit();
		return &quot;保存成功!&quot;;

	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}  

function genguid(){
	var db = null;
	try
	{
            db = new pub.EADatabase();
            var sql = &quot;SELECT sys_guid() FROM DUAL&quot; ; 
            var guid = db.GetSQL(sql);
            return guid;
	}catch(e){
		if( db!= null ) db.Rollback();
		throw new Exception(e);
	}
	finally{
		if(db!=null)db.Close(); 
	}       
}
