//================================================================// 
// 函数：thisOnpost_loaddata
// 说明：调用dbhelper的初始化动作
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：11/17/05 17:31:52
// 修改日志：
//================================================================// 
order = &quot; order by ID&quot;;
function _thisOnpost_loaddata(sheet)
{
       setButton();
       var  fields = &quot;GUID,GUID,sys_guid();&quot;+	
            &quot;LANG,语言,,.+,1;&quot;+            // 第5个字段是否列入查询条件 .+ 非空 \\d 数字
            &quot;SUBTYP,分类,,.+,1;&quot;+            // 第5个字段是否列入查询条件 .+ 非空 \\d 数字
            &quot;CLASS,类名,,.+,1;&quot;+            // 第5个字段是否列入查询条件 .+ 非空 \\d 数字
            &quot;FLAG,标志,,.+,1;&quot;+
            &quot;RETURN, 返回值,,,1;&quot;+
            &quot;FUNCTION, 函数编码,,,1;&quot;+
            &quot;NOTE, 说明,,,1;&quot;+
            &quot;NAME, 函数名称或参数名称,,,1;&quot;+
            &quot;PARAMETER, 参数简要说明,,,1;&quot;+
            &quot;FUNCNOTE, 函数的原形,,,1;&quot;+
            &quot;PARAMNOTE, 参数的具体说明,,,1;&quot;+
            &quot;SAMPLE, 例子,,,1;&quot;+
            &quot;RLSNO, 开始支持的版本号,,,1&quot;;
      var where = &quot;&quot;;// (org =&apos;&quot;+orgid+&quot;&apos; or org is null)  &quot;;
      try {
	      if (LANG!= &quot;&quot; ) {
	      	where += &quot; lang like &apos;&quot;+LANG+&quot;%&apos; &quot;;		
	      }
	}
	catch ( e ) {
		alert ( &quot;LANG参数需要传入&quot; );
	}      
      var orderby = &quot;&quot;;      // 字段的列表
      // 参数1：基表的名称
      // 参数2：字段列表 格式是通过;分隔不同的字段，每个分隔中使用,来分隔不同的字段属性
      //            字段属性： ID,NAME,默认值,正则表达式,是否列入查询的字段(=1是)
      // 参数3：主键
      // 参数4：显示空行标志 =true表示如果结果集为空，隐藏开头的两个空行,强制用户点击“新增”，以保证填入默认值
      // 参数5：查询的表名称，在某些情况下，查询的表名称和基表名称不一样，默认=&quot;&quot;表示查询表名称采用基表名称
      // 参数6：where条件，是不带where的子句如&quot;ID like &apos;01%&apos;&quot;
      // 参数7：orederby，是不带order by的子句如&quot;ID,REFID&quot;, 

      _dbhelper.InitSQL(&quot;FUNCLIST&quot;,fields,&quot;GUID&quot;,false,&quot;FUNCLIST&quot;,where,orderby);
      _dbhelper.InitColumnList(fields );
      _dbhelper.Load(1,1,2,2);                     


       // 自定义的特殊处理
       // 如：
       //       1、设置列的邦定属性
       //       2、设置默认值
       //       3、设置可修改或隐藏属性
}

