//================================================================// 
// 函数：f_dbeditpostloaddata
// 说明：载入每一个页后，做页面的初始化工作
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：11/29/05 11:57:45
// 修改日志：
//================================================================// 
function f_dbeditpostloaddata(){

       var listid1 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_FUNCSUBTYP&quot;),&quot;ID&quot;,&quot;NAME&quot;);
       var listid2 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_FUNCLANG&quot;),&quot;ID&quot;,&quot;NAME&quot;);
       var listid3 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_FUNCFLAG&quot;),&quot;ID&quot;,&quot;NAME&quot;);
       var listid4 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=v_inscodetype&quot;),&quot;ID&quot;,&quot;NAME&quot;);


       for(row=_this.GetMainCellRangeRow1(sheet);row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
       {           
            _this.SetRowHeight(0,row,50);                        
            _this.SetToComboBox(&quot;&quot;, sheet,row,3,listid2);
            _this.SetToComboBox(&quot;&quot;, sheet,row,4,listid1);
            if (LANG==&apos;CODE&apos; || LANG==&apos;MYCODE&apos; )
           	 _this.SetToComboBox(&quot;&quot;, sheet,row,5,listid4 );
            _this.SetToComboBox(&quot;&quot;, sheet,row,6,listid3);
       }
      _this.SetColWidth(0,3,80);
      _this.SetColWidth(0,4,130);
      _this.SetColWidth(0,5,100);

       _this.SetColWidth(0,7,120);
       _this.SetColWidth(0,8,150);


}
