var cursheet=-1;
var currow=-1;
var curcol=-1;
function _thisOnMouseDClick(sheet,row,col)
{
      currow=row;
      curcol=col;
      cursheet=sheet;
      if ( col &gt;=9 && col&lt;=14) 
      	window.open(&quot;ShowXlsGrid.sp?grdid=RTFEDIT&sytid=x&savertf=y&onlytext=y&quot;);
}

//================================================================// 
// 函数：GetRTF
// 说明：由RTFEDIT来调用
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：01/25/07 15:15:58
// 修改日志：
//================================================================// 
function GetRTF()
{
      return _this.GetCellText(cursheet,currow,curcol);
}

//================================================================// 
// 函数：SaveRTF
// 说明：由RTFEDIT来调用
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：01/25/07 15:24:54
// 修改日志：
//================================================================// 
function SaveRTF(str)
{
      _this.SetCellText(cursheet,currow,curcol,str);
      _thisOnCellModify(cursheet,currow,curcol,&quot;&quot;,&quot;&quot;);
}
