ROW.GRDJSDS_VALUE:
var TITLE=&quot;&quot;;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	try{
		updateChart(3);
	}
	catch ( e ) {
		alert ( e );
	}
}

//更新图
function updateChart(xml )
{
	//alert ( parent.all(&quot;_grid&quot;) );
	var xml = parent.frames[1]. f_getcntxml();
	_this.SetMainCellRange(0,2,2,9,4);
	_this.SetXmlDS(0,2,2,9,4,xml);

	var curcol =3;
	//http://wenku.baidu.com/view/d39dee6c011ca300a6c390b1.html
	var curvalcol = curcol;


	var title = &quot;分析图&quot;;

	var xml1  = &quot;&lt;graph xaxisname=&apos;&apos; yaxisname=&apos;&apos; showValues=&apos;0&apos; hovercapbg=&apos;DEDEBE&apos; hovercapborder=&apos;889E6D&apos; rotateNames=&apos;0&apos; numdivlines=&apos;9&apos; divLineColor=&apos;CCCCCC&apos; divLineAlpha=&apos;80&apos; decimalPrecision=&apos;0&apos; showAlternateHGridColor=&apos;1&apos; AlternateHGridAlpha=&apos;30&apos; AlternateHGridColor=&apos;CCCCCC&apos; caption=&apos;&quot;+title +&quot;&apos; subcaption=&apos;&apos; baseFont=&apos;Arial&apos; baseFontSize=&apos;12&apos; &gt; &quot;;//yAxisMaxValue=&apos;100&apos; 
	xml1+=&quot; &lt;categories font=&apos;宋体&apos; fontSize=&apos;12&apos; fontColor=&apos;000000&apos;&gt;&quot;;
	for ( var i=_this.GetMainCellRangeRow1(0);i&lt;=_this.GetMainCellRangeRow2(0);i++ ) {
		var str = _this.GetCellText(0,i,curvalcol -1);
		if ( _this.GetCellText(0,i,2)!=&quot;&quot; ) {
			xml1+=&quot;&lt;category name=&apos;&quot;+str+&quot;&apos; /&gt;&quot;;
		}
//		if ( _this.GetCellText(0,i,1)!=&quot;&quot; )
//			xml1+=&quot;&lt;category name=&apos;&quot;+str+&quot;&apos; hoverText=&apos;&quot;+str&quot;&apos;/&gt;&quot;;
	}
	xml1+=&quot; &lt;/categories&gt; &quot;;



		xml1+=&quot; &lt;dataset seriesname=&apos;数量&apos;&quot;;
		//if ( c== G_VALCOL2 && G_VALCOL!=G_VALCOL2  ) 
		//	xml1+=&quot; parentYAxis=\&quot;S\&quot; &quot;;
		xml1+=&quot; &gt;&quot;;//color=&apos;FDC12E&apos;
		for ( var i=_this.GetMainCellRangeRow1(0);i&lt;=_this.GetMainCellRangeRow2(0);i++ ) {
			var str = _this.GetCellText(0,i,curvalcol );
			if ( _this.GetCellText(0,i,2)!=&quot;&quot; ) 
				xml1+=&quot;&lt;set value=&apos;&quot;+str +&quot;&apos; /&gt;&quot;;
		}
		xml1+=&quot;&lt;/dataset&gt;&quot;;
	xml1+=&quot;&lt;/graph&gt;&quot;;
	parent.f_setchartxml1(xml1);


	var sum = 0;
	for ( var i=_this.GetMainCellRangeRow1(0);i&lt;=_this.GetMainCellRangeRow2(0);i++ ) {
			var str = _this.GetCellText(0,i,curvalcol);
			if ( _this.GetCellText(0,i,2)!=&quot;&quot; ) 
				sum+=1.0*str;

	}


	var xml2  = &quot;&lt;graph showNames=&apos;1&apos;  decimalPrecision=&apos;0&apos; caption=&apos;&quot;+title +&quot;&apos; baseFont=&apos;Arial&apos; baseFontSize=&apos;12&apos; decimalPrecision=&apos;0&apos;  isSliced=&apos;1&apos; showPercentValues=&apos;0&apos; showPercentInToolTip=&apos;1&apos;&gt;&quot;;// 
	xml2+=&quot;&lt;set name=&apos;合计&apos; value=&apos;&quot;+sum +&quot;&apos; /&gt;&quot;;
	for ( var i=_this.GetMainCellRangeRow1(0);i&lt;=_this.GetMainCellRangeRow2(0);i++ ) {
			var str = _this.GetCellText(0,i,curvalcol);
			if ( _this.GetCellText(0,i,2)!=&quot;&quot; ) 
				xml2+=&quot;&lt;set name=&apos;&quot;+_this.GetCellText(0,i,curvalcol-1)+&quot;&apos; value=&apos;&quot;+(1*str+1) +&quot;&apos; /&gt;&quot;;

	}
  	xml2+=&quot;&lt;/graph&gt;&quot;;

	parent.f_setchartxml2(xml2);


	parent.f_chgchart();
	if( _this.GetReleaseNo()&lt;&quot;7.22&quot;){
		 if(confirm( &quot;提示，要使用BI工具的所有功能，请升级控件到7.22以上版本，是否继续？&quot; )==1){
		 	openWindow( &quot;http://xmidware.com/activex/xlsupdate.exe&quot; );
		 }
	}
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if( _this.GetCellText(sheet,row,2)!=&quot;&quot; )
		parent.frames[1].f_showsheet(row);
}

ROW.DATDSC:
select DAT,SUM(QTY),&apos;[%AVGVAL]&apos; AV,0 DD
from(
select DAT,QTY from  v_ss_detail 
where itmid = &apos;[%ITMID]&apos;and lOCID = &apos;[%LOCID]&apos; 
and DAT &lt;= to_date(&apos;[%DAT2]&apos;,&apos;YYYY-MM-DD&apos;) 
and DAT &gt;= to_date(&apos;[%DAT1]&apos;,&apos;YYYY-MM-DD&apos;) 
) group by DAT order by DAT
