function XLSGRID_SetColVisible(sheet,col,flag) {
	if(1*flag&lt;1 ) {
		for(var r=0;r&lt;this.GetRowCount(sheet);r++){
			$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+r+&quot;_&quot;+col).hide();
		}
	}
	else {
		for(var r=0;r&lt;this.GetRowCount(sheet);r++){
			$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+r+&quot;_&quot;+col).show();
		}
	}
}
function XLSGRID_SetRowVisible(sheet,row,flag) {
	if(1*flag&lt;1 ) {
		try{
			document.all(&quot;ROW_&quot;+row).style.display=&quot;none&quot;;
		}catch(e){}
//		$(&quot;#ROW_&quot;+row).hide();
//		document.getElementById(&quot;ROW_&quot;+row)
//		for(var c=0;c&lt;document.getElementById(&quot;table_main&quot;).rows[row].cells.length;c++){
//			try{$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+c).hide();}catch(e){}
//		}
	}
	else {
		$(&quot;#ROW_&quot;+row).show();

//		for(var c=0;c&lt;document.getElementById(&quot;table_main&quot;).rows[row].cells.length;c++){
//			try{$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+c).show();}catch(e){}
//		}

	}
}
