function XLSGRID_GetXml(sheet,row1,col1,row2,col2){
	if(row2-row1&gt;0||col2-col1&gt;0) {
		var xml = &quot;&lt;ROWSET&gt;&quot;;
		for ( var r=1*row1;r&lt;=1*row2;r++){
			xml+=&quot;&lt;ROW&gt;&quot;;
			for( var c=1*col1;c&lt;=1*col2;c++){
				xml+=&quot;&lt;&quot;+_this.GetColName(sheet,c)+&quot;&gt;&quot;+_this.GetCellText(sheet,r,c)+&quot;&lt;/&quot;+_this.GetColName(sheet,c)+&quot;&gt;&quot;;
			}
			xml+=&quot;&lt;/ROW&gt;&quot;;
		}
		xml+=&quot;&lt;/ROWSET&gt;&quot;;
		return xml;
	}else return &quot;&quot;;
}
