function XLSGRID_GetColName(sheet,col){
	if(col&gt;=this.mColNameList.length)return &quot;C&quot;+col;
	if(this.mColNameList[col]==null||this.mColNameList[col]==&quot;null&quot;||this.mColNameList[col]==&quot;&quot;)return &quot;C&quot;+col;
	else return this.mColNameList[col];

}
function XLSGRID_GetColName0(sheet,col){
	if(col&gt;=this.mColNameList.length)return &quot;&quot;;
	if(this.mColNameList[col]==null||this.mColNameList[col]==&quot;null&quot;||this.mColNameList[col]==&quot;&quot;)return &quot;&quot;;
	else return this.mColNameList[col];

}
function XLSGRID_SetCellText(sheet,row,col,str) {
	var obj = document.all(&quot;CELLTEXT_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);
	if(obj==null||obj==undefined) {
		obj = document.all(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);if(obj==null||obj==undefined) return;
		obj.innerHTML = str;
		return;
	}
	var ctrltype = obj.getAttribute(&quot;ctrltype&quot;);
	if(ctrltype == &quot;text&quot;  ||ctrltype == &quot;date&quot;||ctrltype == &quot;selectbox&quot;||ctrltype == &quot;MULTLINE&quot;) {
		obj.setAttribute(&quot;innerval&quot;,str);
		var showtext = obj.getAttribute(&quot;showval&quot;);
		if(showtext!=&quot;&quot;)obj.innerHTML = showtext;
		else obj.innerHTML = str;
	}
	else if(ctrltype == &quot;boolbox&quot; ) {
		if(str==&quot;0&quot;||str==&quot;-1&quot;) obj.checked = false;
		else obj.checked = true;
	}
	else if(ctrltype == &quot;image&quot; ) {
		obj.src=str;
	}	
	else 	obj.value=str;//getAttribute(&quot;innerval&quot;);

//	var ns = document.getElementById(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).childNodes;
//	if (ns.length == 1) {
//		ns[0].innerHTML = str;
//	} else {
//		$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).children(&quot;input&quot;).val(str);
//	}
}
function XLSGRID_GetCellText(sheet,row,col) {
	var str = &quot;&quot;;
	var obj = document.all(&quot;CELLTEXT_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);
	if(obj==null||obj==undefined) {
		obj = document.all(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);if(obj==null||obj==undefined) return &quot;&quot;;
		if(obj.innerHTML.indexOf(&quot;nvlimg.jpg&quot;)&gt;0)return &quot;&quot;;
		return obj.innerHTML;
	}
	var ctrltype = obj.getAttribute(&quot;ctrltype&quot;);
	if(ctrltype == &quot;text&quot; ||ctrltype == &quot;date&quot; ||ctrltype == &quot;selectbox&quot;||ctrltype == &quot;MULTLINE&quot; ) {
		str = obj.getAttribute(&quot;innerval&quot;);
	}
	else if(ctrltype == &quot;boolbox&quot; ) {
		if(obj.checked==true)str = &quot;1&quot;;else str=&quot;0&quot;;
	}
	else if(ctrltype == &quot;image&quot; ) {
		str = obj.src;
	}
	else 	str = obj.value;//getAttribute(&quot;innerval&quot;);

//		$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).children(&quot;input&quot;).each(function(){
//		   str = $(this).val();
//		});
//	}
	var str1=str+&quot;&quot;;
	str1= str1.replace(/\s+/g,&quot;&quot;);
	return str1;
}
function XLSGRID_GetCellShowText(sheet,row,col) {
	var obj = document.all(&quot;CELLTEXT_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);
	if(obj==null||obj==undefined) {
		obj = document.all(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);if(obj==null||obj==undefined) return &quot;&quot;;
		if(obj.innerHTML.indexOf(&quot;nvlimg.jpg&quot;)&gt;0)return &quot;&quot;;
		return obj.innerHTML;
	}

	var ctrltype = obj.getAttribute(&quot;ctrltype&quot;);
	if(ctrltype == &quot;text&quot;  ||ctrltype == &quot;date&quot;||ctrltype == &quot;selectbox&quot;||ctrltype == &quot;MULTLINE&quot;) {
		str = obj.getAttribute(&quot;showval&quot;);
	}
	else if(ctrltype == &quot;combobox&quot; ) {
		str = obj.options[obj.selectedIndex].text;
	}
	else if(ctrltype == &quot;boolbox&quot; ) {
		if(obj.checked==true)str = &quot;1&quot;;else str=&quot;0&quot;;
	}
	else if(ctrltype == &quot;image&quot; ) {
		str = obj.src;
	}
	else 	str = obj.value;
//		var ns = document.getElementById(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col+&quot;_showval&quot;).childNodes;
//		if (ns.length == 1) {
//			return ns[0].innerHTML ;
//		} else {
//			return $(&quot;#CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col+&quot;_showval&quot;).children(&quot;input&quot;).val();
//		}
//	}	
//	var str = &quot;&quot;;
//	$(&quot;#CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).children(&quot;input&quot;).each(function(){
//	   str = $(this).val();
//	});
//	str = str.replace(/\s+/g,&quot;&quot;);
	return str;
}
function XLSGRID_SetCellShowText(sheet,row,col,str) {
	var obj = document.all(&quot;CELLTEXT_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);
	if(obj==null||obj==undefined) {
		obj = document.all(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col);
