		obj.innerHTML =  str;
	}
	var ctrltype = obj.getAttribute(&quot;ctrltype&quot;);
	if(ctrltype == &quot;text&quot;  ||ctrltype == &quot;date&quot;||ctrltype == &quot;selectbox&quot;||ctrltype == &quot;MULTLINE&quot;) {
		obj.setAttribute(&quot;showval&quot;,str);
		var innertext = obj.getAttribute(&quot;innerval&quot;);
		if(str!=&quot;&quot;)obj.innerHTML = str;
		else obj.innerHTML = innertext;
	}
	else if(ctrltype == &quot;boolbox&quot; ) {
		if(str==&quot;0&quot;||str==&quot;-1&quot;) obj.checked = false;
		else obj.checked = true;
	}
	else if(ctrltype == &quot;image&quot; ) {
		obj.src=str;
	}
	else 	obj.value=str;//getAttribute(&quot;innerval&quot;);


//	var ns = document.getElementById(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col+&quot;_showval&quot;).childNodes;
//	if (ns.length == 1) {
//		ns[0].innerHTML = str;
//	} else {
