//	}
//	document.getElementById(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col+&quot;_showval&quot;).style.display=&quot;block&quot;;
//	document.getElementById(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).style.display=&quot;none&quot;;


}
function XLSGRID_GetRowCount(sheet) {
	return document.getElementById(&quot;table_main&quot;).rows.length;
}
function XLSGRID_GetColCount(sheet) {
	return document.getElementById(&quot;table_main&quot;).rows[0].cells.length;
}

function XLSGRID_SetCellColor(sheet,row,col,bkColorR,bkColorG,bkColorB) {
	var r = bkColorR.toString(16);
	var g = bkColorG.toString(16);
	var b = bkColorB.toString(16);

	if (r.length == 1) r = &quot;0&quot;+r;
	if (g.length == 1) g = &quot;0&quot;+g;
	if (b.length == 1) b = &quot;0&quot;+b;

	if (row &gt;= 0 && col &gt;= 0) {
		document.all(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).style.color = &quot;#&quot;+r+g+b;
	}
}
function XLSGRID_SetCellBkColor(sheet,row,col,bkColorR,bkColorG,bkColorB) {
	var r = bkColorR.toString(16);
	var g = bkColorG.toString(16);
	var b = bkColorB.toString(16);

	if (r.length == 1) r = &quot;0&quot;+r;
	if (g.length == 1) g = &quot;0&quot;+g;
	if (b.length == 1) b = &quot;0&quot;+b;

	if (row == -1 && col == -1) {
		document.body.style.background = &quot;#&quot;+r+g+b;
	} else {
		document.all(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col).style.background = &quot;#&quot;+r+g+b;
	}
}
