function XLSGRID_OpenHttpRequest(url,posttype) {
	m_xmlhttpReqList = null
	m_xmlhttpReqList = new Array();//´´½¨Ò»¸öÊı×é;
	m_openurl = url;
	m_openurlposttype = posttype;
}
function XLSGRID_AddHttpRequestParam(name,value,typ) {
	 var obj = new Object();
	obj.reqid=name;
	obj.reqvalue=value;
	obj.reqtyp=typ;
	m_xmlhttpReqList.push(obj);
}
function XLSGRID_HttpCall(url)
{
	this.XLSGRID_OpenHttpRequest(url,&quot;get&quot;);
	var ret=this.XLSGRID_SendHttpRequest();
	this.XLSGRID_CloseHttpRequest();
	return ret.trim();
}
function XLSGRID_SendHttpRequest()  {
	var str = &quot;&quot;;
	xmlhttp=null;
	if (window.XMLHttpRequest)
	{// code for all new browsers
	  xmlhttp=new XMLHttpRequest();
	}
	else if (window.ActiveXObject)
	{// code for IE5 and IE6
	  xmlhttp=new ActiveXObject(&quot;Microsoft.XMLHTTP&quot;);
	}
	if(m_openurlposttype == &quot;GET&quot; ||m_openurlposttype == &quot;get&quot;) {
		if (xmlhttp!=null)
		{
			xmlhttp.open(&quot;GET&quot;,m_openurl,false);
			xmlhttp.send(null);
			//  document.getElementById(&apos;A1&apos;).innerHTML=xmlhttp.status;
			//  document.getElementById(&apos;A2&apos;).innerHTML=xmlhttp.statusText;
			//  document.getElementById(&apos;A3&apos;).innerHTML=xmlhttp.responseText;
			return xmlhttp.responseText;
		}
		else
		{
			alert(&quot;Your browser does not support XMLHTTP.&quot;);
		}
	}else if (m_openurlposttype == &quot;POST&quot;||m_openurlposttype == &quot;post&quot;){
		if (xmlhttp!=null)
		{
			xmlhttp.open(&quot;POST&quot;,m_openurl,false);
			xmlhttp.setRequestHeader(&quot;Content-Type&quot;, &quot;application/x-www-form-urlencoded&quot;)
//			var formData = new FormData();
//			for(var i=0;i&lt;m_xmlhttpReqList.length;i++){
//				var listobj = m_xmlhttpReqList[i];
//				formData.append(listobj.reqid, listobj.reqvalue); // Ìí¼ÓÒ»¸ö±íµ¥Ïî
//			}
			var data = &quot;&quot;;
			for(var i=0;i&lt;m_xmlhttpReqList.length;i++){
				var listobj = m_xmlhttpReqList[i];
				data+=&quot;&&quot;+listobj.reqid+&quot;=&quot;+_this.URLEncodeGBK(listobj.reqvalue);
			}
			xmlhttp.send(data);
			//  document.getElementById(&apos;A1&apos;).innerHTML=xmlhttp.status;
			//  document.getElementById(&apos;A2&apos;).innerHTML=xmlhttp.statusText;
			//  document.getElementById(&apos;A3&apos;).innerHTML=xmlhttp.responseText;
			var resp = xmlhttp.responseText;
			return xmlhttp.responseText;
		}
		else
		{
			alert(&quot;Your browser does not support XMLHTTP.&quot;);
		}

	} else if (m_openurlposttype == &quot;UPLOAD&quot;||m_openurlposttype == &quot;upload&quot;){
		if (xmlhttp!=null)
		{
			xmlhttp.open(&quot;POST&quot;,m_openurl,false);
			var read=new FileReader(&quot;d:/TempConverter.rb&quot;);
			//xmlhttp.setRequestHeader(&quot;Content-Type&quot;, &quot;application/x-www-form-urlencoded&quot;)
			var fd = new FormData();
		        fd.append(&quot;fileToUpload&quot;, document.getElementById(&apos;fileToUpload&apos;).files[0]);
		        xmlhttp.upload.addEventListener(&quot;progress&quot;, uploadProgress, false);
		        xmlhttp.addEventListener(&quot;load&quot;, uploadComplete, false);
		        xmlhttp.addEventListener(&quot;error&quot;, uploadFailed, false);
		        xmlhttp.addEventListener(&quot;abort&quot;, uploadCanceled, false);
		        xmlhttp.send(fd);
			//  document.getElementById(&apos;A1&apos;).innerHTML=xmlhttp.status;
			//  document.getElementById(&apos;A2&apos;).innerHTML=xmlhttp.statusText;
			//  document.getElementById(&apos;A3&apos;).innerHTML=xmlhttp.responseText;
			var resp = xmlhttp.responseText;
			return xmlhttp.responseText;
		}
		else
		{
			alert(&quot;Your browser does not support XMLHTTP.&quot;);
		}
	}

	return &quot;&quot;;
}      
function readFile()
{
	var read=new FileReader();
	read.readAsBinaryString(document.getElementById(&quot;file1&quot;).files[0]);
	read.onloadend=function (e)
	{
		var xhr = new XMLHttpRequest(); 
		xhr.open(&quot;POST&quot;,&quot;fileProgress.asp?fileName=&quot; + file.name); 
		xhr.overrideMimeType(&quot;application/octet-stream&quot;); 
		xhr.sendAsBinary(reader.result); 
	}
}
function XLSGRID_URLEncodeGBK(s) {   
    var a = document.createElement(&quot;a&quot;);   
    function escapeDBC(s) {   
        if (!s) return &quot;&quot;  
        if (window.ActiveXObject) {
	        execScript(&apos;SetLocale &quot;zh-CN&quot;&apos;, &quot;vbscript&quot;); 
        	return s.replace(/[\d\D]/g, function($0) {   
	            window.vbsval = &quot;&quot;;   
	            execScript(&apos;window.vbsval=Hex(Asc(&quot;&apos; + $0 + &apos;&quot;))&apos;, &quot;vbscript&quot;); 
	            return &quot;%&quot; + window.vbsval.slice(0,2) + &quot;%&quot; + window.vbsval.slice(-2);   
	        });
        }
        a.href = &quot;nothing.asp?key=&quot; + s;
        return a.href.split(&quot;key=&quot;).pop();
    }
    return s.replace(/([^\x00-\xff]{0,256})|([\x00-\xff]+)/g, function($0, $1, $2) {   
        return escapeDBC($1) + encodeURIComponent($2||&apos;&apos;);   
    });   
}
