      }
      var rightlayoutid = pubpack.EAFunc.NVL(request.getParameter(&quot;rightlayoutid&quot;),&quot;&quot;);
      var rightlayoutorgid = pubpack.EAFunc.NVL(request.getParameter(&quot;rightlayoutorg&quot;),&quot;&quot;);
      if ( rightlayoutid !=&quot;&quot; ) {
